// src/components/layout/Navbar.jsx
import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import ConfirmationModal from '../common/ConfirmationModal';
import styles from './Navbar.module.css';

// Ícono para el perfil de usuario
const UserIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    className={styles.userIcon}
  >
    <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
    <circle cx="12" cy="7" r="4"></circle>
  </svg>
);

// --- NUEVO ÍCONO PARA CERRAR SESIÓN ---
const LogoutIcon = ({ onClick }) => (
    <div onClick={onClick} className={styles.logoutIconContainer} title="Cerrar Sesión">
        <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
        >
            <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
            <polyline points="16 17 21 12 16 7"></polyline>
            <line x1="21" y1="12" x2="9" y2="12"></line>
        </svg>
    </div>
);


const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [showLogoutModal, setShowLogoutModal] = useState(false);

  const handleLogoutClick = () => {
    setShowLogoutModal(true);
  };

  const confirmLogout = () => {
    logout();
    setShowLogoutModal(false);
    navigate('/');
  };

  return (
    <>
      <nav className={styles.navbar}>
        <div className={styles.navContainer}>
          <Link to="/" className={styles.navLogo}>VERANUM</Link>
          <div className={styles.navMenu}>
            <Link to="/reservar" className={styles.navLink}>Reservar</Link>
            {user && (user.rol === 'admin' || user.rol === 'gerente') && (
              <Link to="/admin" className={styles.navLink}>Admin</Link>
            )}

            <div className={styles.userSection}>
              {user ? (
                <div className={styles.userInfo}>
                  <UserIcon />
                  <span>{user.nombre}</span>
                  {/* --- CAMBIO: USAMOS EL ÍCONO EN LUGAR DEL BOTÓN --- */}
                  <LogoutIcon onClick={handleLogoutClick} />
                </div>
              ) : (
                <Link to="/login" className={styles.loginLink}>
                  <UserIcon />
                  <span>Iniciar Sesión</span>
                </Link>
              )}
            </div>
          </div>
        </div>
      </nav>

      <ConfirmationModal
        show={showLogoutModal}
        title="Cerrar Sesión"
        message="¿Estás seguro de que quieres cerrar la sesión?"
        onConfirm={confirmLogout}
        onCancel={() => setShowLogoutModal(false)}
      />
    </>
  );
};

export default Navbar;
