// src/services/habitacionesService.js
import api from './api';


// Función para obtener TODAS las habitaciones desde la API, ahora con filtros
export const getAllHabitaciones = async () => {
  // Ya no pasamos parámetros, los filtros se harán en el frontend
  const response = await api.get('/habitaciones');
  return response.data;
};
