// src/pages/ReservationsPage.jsx
import React, { useState, useContext, useEffect } from 'react';
import { getAvailableRooms, createReservation } from '../services/reservasService';
import AuthContext from '../context/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';
import styles from './ReservationsPage.module.css';

const ReservationsPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    
    // Leemos la habitación preseleccionada que viene desde la página anterior
    const preselectedRoom = location.state?.preselectedRoom;

    const [step, setStep] = useState(1);
    const [fechas, setFechas] = useState({ inicio: '', fin: '' });
    const [habitaciones, setHabitaciones] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(preselectedRoom || null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    
    const { user } = useContext(AuthContext);
    
    // Si viene una habitación preseleccionada, salta directo al paso 3 al cargar
    useEffect(() => {
        if(preselectedRoom) {
            setStep(3);
        }
    }, [preselectedRoom]);


    const handleSearch = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        try {
            const result = await getAvailableRooms(fechas.inicio, fechas.fin);
            if (result.data.length === 0) {
                setError('No hay habitaciones disponibles para las fechas seleccionadas.');
                setHabitaciones([]);
            } else {
                setHabitaciones(result.data);
                setStep(2); // Siempre vamos al paso 2 para elegir
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Error al buscar habitaciones');
        }
        setLoading(false);
    };

    const handleSelectRoom = (habitacion) => {
        setSelectedRoom(habitacion);
        setStep(3);
    };

    const handleConfirm = async () => {
        if (!user) {
            // Guarda la página actual para volver después del login
            navigate('/login', { state: { from: location } });
            return;
        }
        setLoading(true);
        setError('');
        try {
            const reservationData = {
                habitacion_id: selectedRoom._id,
                fecha_inicio: fechas.inicio,
                fecha_fin: fechas.fin
            };
            await createReservation(reservationData);
            setStep(4); // Paso de confirmación exitosa
        } catch (err) {
            setError(err.response?.data?.message || 'No se pudo crear la reserva.');
        }
        setLoading(false);
    };

    // Si hay una habitación preseleccionada, se muestra su nombre
    const getPageTitle = () => {
        if (preselectedRoom) {
            return `Reservar: ${preselectedRoom.nombre}`;
        }
        return 'Realizar una Reserva';
    };

    return (
        <div className="container">
            <h1>{getPageTitle()}</h1>

            {/* Step 1: Selección de Fechas (Solo si NO hay habitación preseleccionada) */}
            {step === 1 && !preselectedRoom && (
                <div className="card">
                    <h2>Paso 1: Selecciona tus fechas</h2>
                    {error && <p className={styles.errorMessage}>{error}</p>}
                    <form onSubmit={handleSearch} className={styles.formGrid}>
                         <div>
                            <label>Fecha de Llegada</label>
                            <input type="date" value={fechas.inicio} onChange={e => setFechas({...fechas, inicio: e.target.value})} required/>
                         </div>
                         <div>
                             <label>Fecha de Salida</label>
                            <input type="date" value={fechas.fin} onChange={e => setFechas({...fechas, fin: e.target.value})} required/>
                         </div>
                        <button type="submit" disabled={loading} className={styles.fullWidthButton}>{loading ? 'Buscando...' : 'Buscar Disponibilidad'}</button>
                    </form>
                </div>
            )}

            {/* Step 2: Selección de Habitación */}
            {step === 2 && (
                <div className="card">
                    <h2>Paso 2: Elige tu habitación</h2>
                    <div className={styles.roomGrid}>
                        {habitaciones.map(h => (
                            <div key={h._id} className={styles.roomCard}>
                                <h3>{h.nombre}</h3>
                                <p>{h.descripcion}</p>
                                <p><strong>Capacidad:</strong> {h.capacidad} personas</p>
                                <p><strong>Piso:</strong> {h.piso}</p>
                                <p className={styles.roomPrice}>${h.precio_noche.toFixed(2)} / noche</p>
                                <button onClick={() => handleSelectRoom(h)}>Seleccionar Habitación</button>
                            </div>
                        ))}
                    </div>
                     <button onClick={() => setStep(1)} className={styles.backButton}>Volver</button>
                </div>
            )}

            {/* Step 3: Confirmación */}
            {step === 3 && selectedRoom && (
                <div className="card">
                    <h2>Paso {preselectedRoom ? '2' : '3'}: Confirma tu reserva</h2>
                    {error && <p className={styles.errorMessage}>{error}</p>}
                    
                    {/* Formulario para fechas si se preseleccionó habitación */}
                    {!fechas.inicio && !fechas.fin && (
                         <form onSubmit={(e) => {e.preventDefault(); handleConfirm()}} className={styles.formGrid}>
                             <div>
                                <label>Fecha de Llegada</label>
                                <input type="date" value={fechas.inicio} onChange={e => setFechas({...fechas, inicio: e.target.value})} required/>
                             </div>
                             <div>
                                 <label>Fecha de Salida</label>
                                <input type="date" value={fechas.fin} onChange={e => setFechas({...fechas, fin: e.target.value})} required/>
                             </div>
                        </form>
                    )}

                    <div className={styles.confirmationDetails}>
                        <h3>Detalles de la Habitación</h3>
                        <p><strong>Habitación:</strong> {selectedRoom.nombre}</p>
                        <p><strong>Descripción:</strong> {selectedRoom.descripcion}</p>
                        
                        {fechas.inicio && fechas.fin && (
                            <>
                                <h3>Fechas de la Estadía</h3>
                                <p><strong>Llegada:</strong> {fechas.inicio}</p>
                                <p><strong>Salida:</strong> {fechas.fin}</p>

                                <h3 className={styles.totalPrice}>Total Estimado: ${ ( (new Date(fechas.fin) - new Date(fechas.inicio)) / (1000 * 60 * 60 * 24) * selectedRoom.precio_noche ).toFixed(2)}</h3>
                            </>
                        )}
                    </div>
                    <div>
                        <button onClick={() => preselectedRoom ? navigate('/habitaciones') : setStep(2)} className={styles.backButton}>Volver</button>
                        <button onClick={handleConfirm} disabled={loading || !fechas.inicio || !fechas.fin}>{loading ? 'Confirmando...' : 'Confirmar Reserva'}</button>
                    </div>
                </div>
            )}
            
            {/* Step 4: Mensaje de Éxito */}
            {step === 4 && (
                <div className="card" style={{ textAlign: 'center' }}>
                    <h2>¡Reserva Exitosa!</h2>
                    <p>Hemos enviado una confirmación a tu correo electrónico.</p>
