// src/pages/ReservationsPage.jsx
import React, { useState } from 'react';
import { getAvailableRooms } from '../services/reservasService';

const ReservationsPage = () => {
    const [fechas, setFechas] = useState({ inicio: '', fin: '' });
    const [habitaciones, setHabitaciones] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSearch = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        try {
            const result = await getAvailableRooms(fechas.inicio, fechas.fin);
            setHabitaciones(result.data);
        } catch (err) {
            setError(err.response?.data?.message || 'Error al buscar habitaciones');
        }
        setLoading(false);
    };

    return (
        <div>
            <h2>Realizar una Reserva</h2>
            <form onSubmit={handleSearch}>
                <input type="date" value={fechas.inicio} onChange={e => setFechas({...fechas, inicio: e.target.value})} />
                <input type="date" value={fechas.fin} onChange={e => setFechas({...fechas, fin: e.target.value})} />
                <button type="submit" disabled={loading}>{loading ? 'Buscando...' : 'Buscar Disponibilidad'}</button>
            </form>
            
            {error && <p style={{color: 'red'}}>{error}</p>}

            <div>
                {habitaciones.map(h => (
                    <div key={h._id} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
                        <h3>{h.tipo} - ${h.precio_noche}/noche</h3>
                        <p>{h.descripcion}</p>
                        <button>Seleccionar</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ReservationsPage;
