// src/pages/ManageReservasPage.jsx
import React, { useState, useEffect } from 'react';
import { getReservas, deleteReserva, createReservaAdmin } from '../services/adminService';
import ReservaModal from '../components/admin/ReservaModal';
import ConfirmationModal from '../components/common/ConfirmationModal';
import styles from './ManageReservasPage.module.css';

const ManageReservasPage = () => {
    const [reservas, setReservas] = useState([]);
    const [filteredReservas, setFilteredReservas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingReserva, setEditingReserva] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [categoryFilter, setCategoryFilter] = useState('all');
    const [costFilter, setCostFilter] = useState('');

    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [deletingId, setDeletingId] = useState(null);

    // --- FUNCIÓN COMPLETA ---
    const fetchReservas = async () => {
        try {
            setLoading(true);
            const response = await getReservas();
            setReservas(response.data);
            setFilteredReservas(response.data);
        } catch (err) {
            setError('No se pudieron cargar las reservas.');
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchReservas();
    }, []);

    useEffect(() => {
        let result = reservas;
        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase();
            result = result.filter(r => 
                (r.numeroReserva?.toString().includes(lowercasedTerm)) ||
                (r._id.toLowerCase().includes(lowercasedTerm))
            );
        }
        if (categoryFilter !== 'all') { result = result.filter(r => r.habitacion_id?.nombre === categoryFilter); }
        if (costFilter) { result = result.filter(r => r.precio_final >= Number(costFilter)); }
        setFilteredReservas(result);
    }, [searchTerm, categoryFilter, costFilter, reservas]);

    const handleDeleteClick = (id) => {
        setDeletingId(id);
        setShowDeleteModal(true);
    };

    const confirmDelete = async () => {
        if (deletingId) {
            try {
                await deleteReserva(deletingId);
                fetchReservas();
            } catch (err) {
                alert('No se pudo eliminar la reserva.');
            } finally {
                setShowDeleteModal(false);
                setDeletingId(null);
            }
        }
    };

    const formatDate = (dateString) => new Date(dateString).toLocaleDateString();
    
    // --- FUNCIÓN COMPLETA ---
    const handleOpenModal = (reserva = null) => {
        setEditingReserva(reserva);
        setIsModalOpen(true);
    };

    // --- FUNCIÓN COMPLETA ---
    const handleCloseModal = () => {
        setIsModalOpen(false);
        setEditingReserva(null);
    };

    // --- FUNCIÓN COMPLETA ---
    const handleSaveReserva = async (formData) => {
        try {
            if (editingReserva) {
                // Lógica futura para editar
            } else {
                await createReservaAdmin(formData);
            }
            fetchReservas();
            handleCloseModal();
        } catch (err) {
            alert('Error al guardar la reserva.');
        }
    };
    
    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Reservas</h1>
                <button onClick={() => handleOpenModal()} className={styles.addButton}>Crear Nueva Reserva</button>
            </div>
            <div className={`card ${styles.filtersCard}`}>
                <input type="text" placeholder="Buscar por Nº de Reserva..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
                 <select value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}>
                    <option value="all">Todas las Categorías</option>
                    <option value="Doble Clásica">Doble Clásica</option>
                    <option value="Matrimonial Superior">Matrimonial Superior</option>
                    <option value="Individual Ejecutiva">Individual Ejecutiva</option>
                </select>
                <input type="number" placeholder="Filtrar por costo mínimo..." value={costFilter} onChange={(e) => setCostFilter(e.target.value)} />
            </div>
            <div className={styles.tableContainer}>
                {loading && <p>Cargando...</p>}
                {error && <p className={styles.errorMessage}>{error}</p>}
                {!loading && !error && (
                    <table>
                        <thead>
                            <tr>
                                <th>Nº Reserva</th>
                                <th>Cliente</th>
                                <th>Habitación</th>
                                <th>Check-in</th>
                                <th>Check-out</th>
                                <th>Costo</th>
                                <th>Estado</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredReservas.length > 0 ? filteredReservas.map(reserva => (
                                <tr key={reserva._id}>
                                    <td data-label="Nº Reserva">{reserva.numeroReserva}</td>
                                    <td data-label="Cliente">{reserva.cliente_id?.nombre}</td>
                                    <td data-label="Habitación">{reserva.habitacion_id?.nombre}</td>
                                    <td data-label="Check-in">{formatDate(reserva.fecha_inicio)}</td>
                                    <td data-label="Check-out">{formatDate(reserva.fecha_fin)}</td>
                                    <td data-label="Costo">${reserva.precio_final.toFixed(0)}</td>
                                    <td data-label="Estado"><span className={`${styles.status} ${styles[`status_${reserva.estado?.toLowerCase()}`]}`}>{reserva.estado}</span></td>
                                    <td data-label="Acciones">
                                        <button className={styles.actionButton}>Editar</button>
                                        <button onClick={() => handleDeleteClick(reserva._id)} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                    </td>
                                </tr>
                            )) : (
                                <tr><td colSpan="8">No se encontraron reservas.</td></tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
            <ReservaModal show={isModalOpen} onClose={handleCloseModal} onSave={handleSaveReserva} reserva={editingReserva} />
            <ConfirmationModal 
                show={showDeleteModal}
                title="Confirmar Eliminación"
                message="¿Estás seguro de que quieres eliminar esta reserva de forma permanente?"
                onConfirm={confirmDelete}
                onCancel={() => setShowDeleteModal(false)}
            />
        </div>
    );
};

export default ManageReservasPage;
