import React, { useState, useEffect, useContext } from 'react';
import AuthContext from '../context/AuthContext';
import { getMyProfile } from '../services/usuarioService';
import styles from './MiCuentaPage.module.css';

const MiCuentaPage = () => {
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { user } = useContext(AuthContext);

    useEffect(() => {
        const fetchProfile = async () => {
            if (!user) return;
            try {
                const response = await getMyProfile();
                setProfile(response.data);
            } catch (err) {
                setError('No se pudo cargar la información de tu perfil.');
            } finally {
                setLoading(false);
            }
        };

        fetchProfile();
    }, [user]);

    if (loading) {
        return <div className="container"><p>Cargando perfil...</p></div>;
    }

    if (error) {
        return <div className="container"><p className={styles.error}>{error}</p></div>;
    }

    if (!profile) {
        return <div className="container"><p>No se encontró la información del perfil.</p></div>;
    }

    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Mi Perfil</h1>
                <p>Aquí puedes ver la información de tu cuenta.</p>
            </div>
            <div className={`card ${styles.profileCard}`}>
                <div className={styles.profileField}>
                    <label>Nombre Completo</label>
                    <span>{profile.nombre}</span>
                </div>
                <div className={styles.profileField}>
                    <label>RUN</label>
                    <span>{profile._id}</span>
                </div>
                <div className={styles.profileField}>
                    <label>Email</label>
                    <span>{profile.email}</span>
                </div>
                <div className={styles.profileField}>
                    <label>Rol</label>
                    <span className={styles.role}>{profile.rol}</span>
                </div>
                <div className={styles.profileField}>
                    <label>Miembro desde</label>
                    <span>{new Date(profile.createdAt).toLocaleDateString('es-CL')}</span>
                </div>
                 <div className={styles.editInfo}>
                    <p>Para modificar tus datos, por favor contacta con soporte.</p>
                </div>
            </div>
        </div>
    );
};

export default MiCuentaPage;
