// src/pages/ManageUsuariosPage.jsx
import React, { useState, useEffect } from 'react';
import { getAllUsers } from '../services/adminService';
import styles from './ManageReservasPage.module.css'; // Reutilizamos los mismos estilos

const ManageUsuariosPage = () => {
    const [users, setUsers] = useState([]);
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    
    const [searchTerm, setSearchTerm] = useState('');
    const [sortOrder, setSortOrder] = useState('desc'); // 'desc' para más nuevos, 'asc' para más antiguos

    const fetchUsers = async () => {
        try {
            setLoading(true);
            const response = await getAllUsers();
            setUsers(response.data);
        } catch (err) {
            setError('No se pudieron cargar los usuarios.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    useEffect(() => {
        let result = [...users];

        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase().trim();
            result = result.filter(user => user._id.toLowerCase().includes(lowercasedTerm));
        }

        result.sort((a, b) => {
            const dateA = new Date(a.createdAt);
            const dateB = new Date(b.createdAt);
            return sortOrder === 'asc' ? dateA - dateB : dateB - dateA;
        });
        
        setFilteredUsers(result);
    }, [users, searchTerm, sortOrder]);

    const handleClearFilters = () => {
        setSearchTerm('');
        setSortOrder('desc');
    };

    const formatDate = (dateString) => new Date(dateString).toLocaleDateString('es-CL');

    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Usuarios</h1>
                <button onClick={() => alert('Función para crear usuario pendiente.')} className={styles.addButton}>
                    Crear Nuevo Usuario
                </button>
            </div>
            
            <div className={`card ${styles.filtersCard}`}>
                <div className={styles.searchContainer}>
                    <div className={styles.filterGroup}>
                        <label htmlFor="search">Buscar por RUN</label>
                        <input 
                            id="search"
                            type="text" 
                            placeholder="Ingrese RUN del cliente..." 
                            value={searchTerm} 
                            onChange={(e) => setSearchTerm(e.target.value)} 
                        />
                    </div>
                    <button onClick={handleClearFilters} className={styles.clearFiltersButton}>
                        Borrar Filtros
                    </button>
                </div>
                <div className={styles.filterGroup}>
                    <label htmlFor="sort">Ordenar por Creación</label>
                    <select id="sort" value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
                        <option value="desc">Más Nuevos Primero</option>
                        <option value="asc">Más Antiguos Primero</option>
                    </select>
                </div>
            </div>

            <div className={styles.tableContainer}>
                {loading ? <p>Cargando...</p> : error ? <p className={styles.errorMessage}>{error}</p> : (
                    <table>
                        <thead>
                            <tr>
                                <th>RUN Cliente</th>
                                <th>Nombre</th>
                                <th>Email</th>
                                <th>Rol</th>
                                <th>Miembro Desde</th>
                                <th>Última Conexión</th>
                                <th>Nº Reservas</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredUsers.length > 0 ? filteredUsers.map(user => (
                                <tr key={user._id}>
                                    <td data-label="RUN Cliente">{user._id}</td>
                                    <td data-label="Nombre">{user.nombre}</td>
                                    <td data-label="Email">{user.email}</td>
                                    <td data-label="Rol">{user.rol}</td>
                                    <td data-label="Miembro Desde">{formatDate(user.createdAt)}</td>
                                    <td data-label="Última Conexión">{'N/A'}</td>
                                    <td data-label="Nº Reservas">{user.numeroDeReservas}</td>
                                    <td data-label="Acciones">
                                        <div className={styles.actionsContainer}>
                                            <button onClick={() => alert('Función para editar pendiente.')} className={styles.actionButton}>Editar</button>
                                            <button onClick={() => alert('Función para eliminar pendiente.')} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                        </div>
                                    </td>
                                </tr>
                            )) : (
                                <tr><td colSpan="8">No se encontraron usuarios.</td></tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default ManageUsuariosPage;
