// src/components/admin/HabitacionModal.jsx
import React, { useState, useEffect } from 'react';
import styles from './ReservaModal.module.css'; // Reutilizamos estilos

const HabitacionModal = ({ show, onClose, onSave, habitacion, hoteles }) => {
    const [formData, setFormData] = useState({
        hotel_id: '',
        _id: '',
        nombre: '',
        descripcion: '',
        categoria: 'individual',
        capacidad: '1',
        piso: '1',
        precio: '',
        servicios: ''
    });

    // --- CORRECCIÓN: useEffect adaptado a la nueva estructura de datos ---
    useEffect(() => {
        if (show && habitacion) {
            // Modo Edición: Llenar el formulario con los datos de la habitación existente
            setFormData({
                hotel_id: habitacion.hotel_id || '',
                _id: habitacion._id || '', // El _id es el número de habitación
                nombre: habitacion.nombre || '',
                descripcion: habitacion.descripcion || '',
                categoria: habitacion.categoria || 'individual',
                capacidad: habitacion.capacidad?.toString() || '1',
                piso: habitacion.piso?.toString() || '1',
                precio: habitacion.precio || '',
                servicios: habitacion.servicios ? habitacion.servicios.join(', ') : ''
            });
        } else {
            // Modo Creación: Resetear el formulario
            setFormData({
                _id: '',
                nombre: '',
                descripcion: '',
                categoria: 'individual',
                capacidad: '1',
                piso: '1',
                precio: '',
                servicios: ''
            });
        }
    }, [habitacion, show]);

    if (!show) {
        return null;
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // --- CORRECCIÓN: Preparamos los datos con los tipos correctos antes de guardar ---
        const dataToSave = {
            ...formData,
            // Si es una nueva habitación, no enviamos el _id para que se autogenere
            _id: habitacion ? formData._id : undefined, 
            capacidad: parseInt(formData.capacidad, 10),
            piso: parseInt(formData.piso, 10),
            precio: parseFloat(formData.precio),
            servicios: formData.servicios.split(',').map(s => s.trim()).filter(s => s)
        };
        onSave(dataToSave);
    };

    return (
        <div className={styles.modalOverlay}>
            <div className={`${styles.modalContent} ${styles.smallModal}`}>
                <h2>{habitacion ? `Editar Habitación Nº ${habitacion._id}` : 'Crear Nueva Habitación'}</h2>
                <form onSubmit={handleSubmit}>
                    
                    {/* --- NUEVO: Campo para el número de habitación (solo visible al editar) --- */}
                    {!habitacion && (
                        <div className={styles.inputGroup}>
                            <label>Número de Habitación (_id)</label>
                            <input type="number" name="_id" value={formData._id} onChange={handleChange} required={!habitacion} />
                        </div>
                    )}

                    <div className={styles.inputGroup}>
                        <label>Nombre</label>
                        <input type="text" name="nombre" value={formData.nombre} onChange={handleChange} required />
                    </div>
                    <div className={styles.inputGroup}>
                        <label>Descripción</label>
                        <textarea name="descripcion" value={formData.descripcion} onChange={handleChange} rows="4" required />
                    </div>
                    <div className={styles.inputGroup}>
                        <label>Categoría</label>
                        <select name="categoria" value={formData.categoria} onChange={handleChange}>
                            <option value="individual">Individual</option>
                            <option value="doble">Doble</option>
                            <option value="matrimonial">Matrimonial</option>
                        </select>
                    </div>
                    <div className={styles.inputGroup}>
                        <label>Capacidad (personas)</label>
                        <select name="capacidad" value={formData.capacidad} onChange={handleChange} required>
                            <option value="1">1 persona</option>
                            <option value="2">2 personas</option>
                            <option value="3">3 personas</option>
                            <option value="4">4 personas</option>
                        </select>
                    </div>
                    <div className={styles.inputGroup}>
                        <label>Piso</label>
                        <select name="piso" value={formData.piso} onChange={handleChange} required>
                            <option value="1">Piso 1</option>
                            <option value="2">Piso 2</option>
                            <option value="3">Piso 3</option>
                        </select>
                    </div>
                    <div className={styles.inputGroup}>
                        {/* --- CORRECCIÓN: 'name' y 'value' usan 'precio' --- */}
                        <label>Precio por Noche</label>
                        <input type="number" name="precio" value={formData.precio} onChange={handleChange} required min="0" />
                    </div>
                     <div className={styles.inputGroup}>
                        <label>Servicios (separados por coma)</label>
                        <input type="text" name="servicios" value={formData.servicios} onChange={handleChange} placeholder="WiFi, TV Cable, etc." />
                    </div>
                    <div className={styles.buttonGroup}>
                        <button type="button" onClick={onClose} className={styles.cancelButton}>Cancelar</button>
                        <button type="submit">Guardar Habitación</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default HabitacionModal;
