// src/pages/ManageUsuariosPage.jsx
import React, { useState, useEffect } from 'react';
import { getAllUsers, getReservasByRun } from '../services/adminService';
import UserReservationsModal from '../components/admin/UserReservationsModal';
import styles from './ManageReservasPage.module.css';

const ManageUsuariosPage = () => {
    const [users, setUsers] = useState([]);
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    
    const [searchTerm, setSearchTerm] = useState('');
    const [roleFilter, setRoleFilter] = useState('all');
    const [reservasSort, setReservasSort] = useState('default');
    const [creationSort, setCreationSort] = useState('desc');

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalLoading, setModalLoading] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);
    const [selectedUserReservations, setSelectedUserReservations] = useState([]);

    const fetchUsers = async () => {
        try {
            setLoading(true);
            const response = await getAllUsers();
            setUsers(response.data);
        } catch (err) {
            setError('No se pudieron cargar los usuarios.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    useEffect(() => {
        let result = [...users];

        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase().trim();
            result = result.filter(user => user._id.toLowerCase().includes(lowercasedTerm));
        }

        if (roleFilter !== 'all') {
            result = result.filter(user => user.rol === roleFilter);
        }

        if (reservasSort !== 'default') {
            result.sort((a, b) => {
                return reservasSort === 'desc' 
                    ? b.numeroDeReservas - a.numeroDeReservas 
                    : a.numeroDeReservas - b.numeroDeReservas;
            });
        } else {
            result.sort((a, b) => {
                const dateA = new Date(a.createdAt);
                const dateB = new Date(b.createdAt);
                return creationSort === 'asc' ? dateA - dateB : dateB - dateA;
            });
        }
        
        setFilteredUsers(result);
    }, [users, searchTerm, roleFilter, reservasSort, creationSort]);

    const handleClearFilters = () => {
        setSearchTerm('');
        setRoleFilter('all');
        setReservasSort('default');
        setCreationSort('desc');
    };

    const handleOpenReservasModal = async (user) => {
        setSelectedUser(user);
        setIsModalOpen(true);
        setModalLoading(true);
        try {
            const response = await getReservasByRun(user._id);
            setSelectedUserReservations(response.data);
        } catch (err) {
            alert('No se pudo cargar el historial de reservas.');
        } finally {
            setModalLoading(false);
        }
    };

    const handleCloseReservasModal = () => {
        setIsModalOpen(false);
        setSelectedUser(null);
        setSelectedUserReservations([]);
    };

    const formatDate = (dateString) => new Date(dateString).toLocaleDateString('es-CL');

    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Usuarios</h1>
                <button onClick={() => alert('Función para crear usuario pendiente.')} className={styles.addButton}>
                    Crear Nuevo Usuario
                </button>
            </div>
            
            <div className={`card ${styles.filtersCard}`}>
                {/* --- CAMBIO: Se revierte la estructura del filtro --- */}
                <div className={styles.searchContainer}>
                    <div className={styles.filterGroup}>
                        <label htmlFor="search">Buscar por RUN</label>
                        <input 
                            id="search"
                            type="text" 
                            placeholder="Ingrese RUN del cliente..." 
                            value={searchTerm} 
                            onChange={(e) => setSearchTerm(e.target.value)} 
                        />
                    </div>
                    <button onClick={handleClearFilters} className={styles.clearFiltersButton}>
                        Borrar Filtros
                    </button>
                </div>
                <div className={styles.filterGroup}>
                    <label htmlFor="roleFilter">Filtrar por Rol</label>
                    <select id="roleFilter" value={roleFilter} onChange={(e) => setRoleFilter(e.target.value)}>
                        <option value="all">Todos los Roles</option>
                        <option value="cliente">Cliente</option>
                        <option value="gerente">Gerente</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
                <div className={styles.filterGroup}>
                    <label htmlFor="reservasSort">Ordenar por Nº Reservas</label>
                    <select id="reservasSort" value={reservasSort} onChange={(e) => setReservasSort(e.target.value)}>
                        <option value="default">Por Defecto</option>
                        <option value="desc">Mayor a Menor</option>
                        <option value="asc">Menor a Mayor</option>
                    </select>
                </div>
            </div>

            <div className={styles.tableContainer}>
                {loading ? <p>Cargando...</p> : error ? <p className={styles.errorMessage}>{error}</p> : (
                    <table>
                        <thead>
                            <tr>
                                <th>RUN Cliente</th>
                                <th>Nombre</th>
                                <th>Email</th>
                                <th>Rol</th>
                                <th>Miembro Desde</th>
                                <th>Reservas</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredUsers.length > 0 ? filteredUsers.map(user => (
                                <tr key={user._id}>
                                    <td data-label="RUN Cliente">{user._id}</td>
                                    <td data-label="Nombre">{user.nombre}</td>
                                    <td data-label="Email">{user.email}</td>
                                    <td data-label="Rol">{user.rol}</td>
                                    <td data-label="Miembro Desde">{formatDate(user.createdAt)}</td>
                                    <td data-label="Nº Reservas">
                                        {user.numeroDeReservas > 0 ? (
                                            <button onClick={() => handleOpenReservasModal(user)} className={styles.viewReservaButton}>
                                                Ver ({user.numeroDeReservas})
                                            </button>
                                        ) : ( '0' )}
                                    </td>
                                    <td data-label="Acciones">
                                        <div className={styles.actionsContainer}>
                                            <button onClick={() => alert('Función para editar pendiente.')} className={styles.actionButton}>Editar</button>
                                            <button onClick={() => alert('Función para eliminar pendiente.')} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                        </div>
                                    </td>
                                </tr>
                            )) : (
                                <tr><td colSpan="7">No se encontraron usuarios.</td></tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
            <UserReservationsModal 
                show={isModalOpen}
                onClose={handleCloseReservasModal}
                user={selectedUser}
                reservations={selectedUserReservations}
                loading={modalLoading}
            />
        </div>
    );
};

export default ManageUsuariosPage;
