// src/components/layout/Navbar.jsx
import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import ConfirmationModal from '../common/ConfirmationModal'; // Importamos la nueva modal
import styles from './Navbar.module.css';

// Creamos el ícono como un componente de React para poder estilizarlo fácilmente
const UserIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    className={styles.userIcon}
  >
    <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
    <circle cx="12" cy="7" r="4"></circle>
  </svg>
);


const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [showLogoutModal, setShowLogoutModal] = useState(false);

  // Esta función ahora solo abre la modal
  const handleLogoutClick = () => {
    setShowLogoutModal(true);
  };

  // Esta función se ejecuta si el usuario confirma en la modal
  const confirmLogout = () => {
    logout();
    setShowLogoutModal(false);
    navigate('/');
  };

  return (
    <>
      <nav className={styles.navbar}>
        <div className={styles.navContainer}>
          <Link to="/" className={styles.navLogo}>VERANUM</Link>
          <div className={styles.navMenu}>
            <Link to="/reservar" className={styles.navLink}>Reservar</Link>
            {user && (user.rol === 'admin' || user.rol === 'gerente') && (
              <Link to="/admin" className={styles.navLink}>Admin</Link>
            )}

            {/* --- SECCIÓN DE USUARIO ACTUALIZADA --- */}
            <div className={styles.userSection}>
              {user ? (
                <div className={styles.userInfo}>
                  <UserIcon />
                  <span>{user.nombre}</span>
                  <button onClick={handleLogoutClick} className={styles.logoutButton}>SALIR</button>
                </div>
              ) : (
                <Link to="/login" className={styles.loginLink}>
                  <UserIcon />
                  <span>Iniciar Sesión</span>
                </Link>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* --- RENDERIZADO DE LA MODAL --- */}
      <ConfirmationModal
        show={showLogoutModal}
        title="Cerrar Sesión"
        message="¿Estás seguro de que quieres cerrar la sesión?"
        onConfirm={confirmLogout}
        onCancel={() => setShowLogoutModal(false)}
      />
    </>
  );
};

export default Navbar;
