// src/pages/AdminDashboardPage.jsx
import React, { useContext } from 'react';
import AuthContext from '../context/AuthContext';

const AdminDashboardPage = () => {
    const { user } = useContext(AuthContext);

    // Protección simple en el frontend
    if (!user || (user.rol !== 'admin' && user.rol !== 'gerente')) {
        return <p>Acceso denegado. Requiere privilegios de administrador.</p>
    }

    return (
        <div>
            <h2>Panel de Administración</h2>
            <p>Bienvenido, {user.nombre}. Aquí se mostrarán los reportes y herramientas de gestión.</p>
            {/* Aquí irían los componentes de reportes */}
        </div>
    );
};

export default AdminDashboardPage;
