// src/pages/ManageHabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { 
    getAllHotelesAdmin, 
    getHabitacionesByHotel, 
    getReservas,
    createHabitacion, 
    updateHabitacion, 
    deleteHabitacion 
} from '../services/adminService';
import HabitacionModal from '../components/admin/HabitacionModal';
import ConfirmationModal from '../components/common/ConfirmationModal';
import Notification from '../components/common/Notification';
import styles from './ManageReservasPage.module.css';

const ManageHabitacionesPage = () => {
    // --- NUEVO: Estados para manejar hoteles y la selección actual ---
    const [hoteles, setHoteles] = useState([]);
    const [selectedHotel, setSelectedHotel] = useState('');
    
    const [habitaciones, setHabitaciones] = useState([]);
    const [filteredHabitaciones, setFilteredHabitaciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Estados para filtros y modales (sin cambios)
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingHabitacion, setEditingHabitacion] = useState(null);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [deletingHabitacion, setDeletingHabitacion] = useState(null);
    const [notification, setNotification] = useState({ show: false, message: '', type: '' });
    const [searchTerm, setSearchTerm] = useState('');
    const [availabilityFilter, setAvailabilityFilter] = useState('all');
    const [priceSort, setPriceSort] = useState('none');

    // --- LÓGICA MODIFICADA: Primero carga los hoteles ---
    useEffect(() => {
        const fetchHoteles = async () => {
            try {
                const response = await getAllHotelesAdmin();
                setHoteles(response.data);
                if (response.data.length > 0) {
                    setSelectedHotel(response.data[0]._id); // Selecciona el primer hotel por defecto
                } else {
                    setLoading(false); // No hay hoteles, no hay nada que cargar
                }
            } catch (err) {
                setError('No se pudieron cargar los hoteles.');
                setLoading(false);
            }
        };
        fetchHoteles();
    }, []);

    // --- LÓGICA MODIFICADA: Carga las habitaciones CADA VEZ que cambia el hotel seleccionado ---
    useEffect(() => {
        if (selectedHotel) {
            const fetchHotelData = async () => {
                setLoading(true);
                try {
                    // Pide las habitaciones y todas las reservas en paralelo
                    const [habitacionesRes, reservasRes] = await Promise.all([
                        getHabitacionesByHotel(selectedHotel),
                        getReservas()
                    ]);
                    
                    const hoy = new Date();
                    const inicioDeHoy = new Date();
                    inicioDeHoy.setHours(0, 0, 0, 0);

                    // Combina los datos para calcular el estado y el cliente actual
                    const habitacionesConDatos = habitacionesRes.data.map(hab => {
                        const reservasParaHabitacion = reservasRes.data.filter(res =>
                            res.habitacion_id?._id === hab._id &&
                            res.estado === 'Confirmado' &&
                            new Date(res.fecha_fin) > inicioDeHoy
                        ).sort((a, b) => new Date(a.fecha_inicio) - new Date(b.fecha_inicio));

                        let reservaRelevante = null;
                        let estadoCalculado = 'Disponible';

                        if (reservasParaHabitacion.length > 0) {
                            const reservaActivaHoy = reservasParaHabitacion.find(res =>
                                new Date(res.fecha_inicio) <= hoy && new Date(res.fecha_fin) > hoy
                            );
                            if (reservaActivaHoy) {
                                reservaRelevante = reservaActivaHoy;
                                estadoCalculado = 'No Disponible';
                            } else {
                                reservaRelevante = reservasParaHabitacion[0];
                                estadoCalculado = 'Reservada';
                            }
                        }
                        return { ...hab, estadoCalculado, clienteActualRUN: reservaRelevante?.cliente_id?._id || null };
                    });

                    setHabitaciones(habitacionesConDatos);
                    setError('');
                } catch (err) {
                    setError('No se pudieron cargar los datos de las habitaciones.');
                } finally {
                    setLoading(false);
                }
            };
            fetchHotelData();
        }
    }, [selectedHotel]);

    // Lógica de filtrado del lado del cliente (sin cambios)
    useEffect(() => {
        let result = [...habitaciones];
        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase();
            result = result.filter(h => h.nombre.toLowerCase().includes(lowercasedTerm) || h._id.toString().includes(lowercasedTerm));
        }
        if (availabilityFilter !== 'all') {
            result = result.filter(h => h.estadoCalculado === availabilityFilter);
        }
        if (priceSort !== 'none') {
            result.sort((a, b) => priceSort === 'desc' ? b.precio - a.precio : a.precio - b.precio);
        }
        setFilteredHabitaciones(result);
    }, [habitaciones, searchTerm, availabilityFilter, priceSort]);

    
    const handleSaveHabitacion = async (formData) => {
        try {
            if (editingHabitacion) {
                await updateHabitacion(editingHabitacion._id, formData);
                setNotification({ show: true, message: 'Habitación actualizada.', type: 'success' });
            } else {
                await createHabitacion(formData);
                setNotification({ show: true, message: 'Habitación creada exitosamente.', type: 'success' });
            }
            // Refresca la lista de habitaciones para el hotel actual
            const response = await getHabitacionesByHotel(selectedHotel);
            setHabitaciones(response.data);
            handleCloseModal();
        } catch (err) {
            setNotification({ show: true, message: `Error: ${err.response?.data?.message || err.message}`, type: 'error' });
        }
    };
    
    // ... (resto de tus handlers: handleOpenModal, handleCloseModal, handleDeleteClick, confirmDelete, etc., se mantienen igual)

    return (
        <>
            <Notification show={notification.show} message={notification.message} type={notification.type} />
            <div className="container">
                <div className={styles.header}>
                    <h1>Gestión de Habitaciones</h1>
                    <button onClick={() => handleOpenModal(null)} className={styles.addButton} disabled={!hoteles || hoteles.length === 0}>
                        Crear Nueva Habitación
                    </button>
                </div>

                {/* --- NUEVO: Selector de Hotel --- */}
                {hoteles.length > 0 ? (
                    <div className="card" style={{ padding: '1.5rem', marginBottom: '2rem' }}>
                        <label htmlFor="hotel-selector" style={{ fontWeight: 700, marginRight: '1rem', fontSize: '1.1rem' }}>
                            Gestionando Hotel:
                        </label>
                        <select id="hotel-selector" value={selectedHotel} onChange={(e) => setSelectedHotel(e.target.value)}>
                            {hoteles.map(hotel => (
                                <option key={hotel._id} value={hotel._id}>{hotel.nombre}</option>
                            ))}
                        </select>
                    </div>
                ) : !loading && (
                    <div className="card" style={{padding: '1.5rem'}}><p>No hay hoteles creados. Por favor, cree un hotel primero en "Gestión de Hoteles".</p></div>
                )}

                {/* El resto de la página solo se muestra si hay un hotel seleccionado */}
                {selectedHotel && (
                    <>
                        <div className={`card ${styles.filtersCard}`}>
                            {/* ... tus filtros de Buscar, Disponibilidad y Precio ... */}
                        </div>

                        <div className={styles.tableContainer}>
                            {loading ? <p>Cargando habitaciones...</p> : error ? <p className={styles.errorMessage}>{error}</p> : (
                                <table>
                                    {/* ... tu thead y tbody para las habitaciones ... */}
                                    {/* El .map debe usar 'filteredHabitaciones' */}
                                </table>
                            )}
                        </div>
                    </>
                )}

                <HabitacionModal 
                    show={isModalOpen} 
                    onClose={handleCloseModal} 
                    onSave={handleSaveHabitacion} 
                    habitacion={editingHabitacion}
                    hoteles={hoteles}
                    defaultHotelId={selectedHotel}
                />
                <ConfirmationModal 
                    show={showDeleteModal}
                    title="Confirmar Eliminación"
                    message={`¿Seguro que quieres eliminar la habitación ${deletingHabitacion?._id}?`}
                    onConfirm={confirmDelete}
                    onCancel={() => setShowDeleteModal(false)}
                />
            </div>
        </>
    );
};

export default ManageHabitacionesPage;
