// src/pages/ManageReservasPage.jsx
import React, { useState, useEffect } from 'react';
import { getReservas, deleteReserva, createReservaAdmin, updateReserva } from '../services/adminService';
import ReservaModal from '../components/admin/ReservaModal';
import ConfirmationModal from '../components/common/ConfirmationModal';
import styles from './ManageReservasPage.module.css';

// --- Íconos para los filtros ---
const SearchIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>;
const FilterIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polygon points="22 3 2 3 10 12.46 10 19 14 21 14 12.46 22 3"></polygon></svg>;


const ManageReservasPage = () => {
    const [reservas, setReservas] = useState([]);
    const [filteredReservas, setFilteredReservas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingReserva, setEditingReserva] = useState(null);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [deletingId, setDeletingId] = useState(null);

    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');

    const fetchReservas = async () => { /* ... (código sin cambios) ... */ };
    useEffect(() => { fetchReservas(); }, []);

    // --- LÓGICA DE FILTRADO CORREGIDA ---
    useEffect(() => {
        let result = reservas;

        // Búsqueda por Nº Reserva
        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase().trim();
            result = result.filter(r => 
                // Convertimos el número a string para poder buscar
                r.numeroReserva?.toString().includes(lowercasedTerm)
            );
        }
        
        // Filtro por Estado
        if (statusFilter !== 'all') {
            result = result.filter(r => r.estado === statusFilter);
        }

        setFilteredReservas(result);
    }, [searchTerm, statusFilter, reservas]);


    const handleDeleteClick = (id) => { /* ... (código sin cambios) ... */ };
    const confirmDelete = async () => { /* ... (código sin cambios) ... */ };
    const formatDate = (dateString) => new Date(dateString).toLocaleDateString('es-CL');
    const handleOpenModal = (reserva = null) => { /* ... (código sin cambios) ... */ };
    const handleCloseModal = () => { /* ... (código sin cambios) ... */ };
    const handleSaveReserva = async (formData) => { /* ... (código sin cambios) ... */ };
    
    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Reservas</h1>
                <button onClick={() => handleOpenModal()} className={styles.addButton}>Crear Nueva Reserva</button>
            </div>

            {/* --- BARRA DE FILTROS REDISEÑADA --- */}
            <div className={`card ${styles.filtersCard}`}>
                <div className={styles.filterGroup}>
                    <label htmlFor="search">Buscar Reserva</label>
                    <div className={styles.inputWrapper}>
                        <SearchIcon />
                        <input 
                            id="search"
                            type="text" 
                            placeholder="Nº de Reserva..." 
                            value={searchTerm} 
                            onChange={(e) => setSearchTerm(e.target.value)} 
                        />
                    </div>
                </div>
                <div className={styles.filterGroup}>
                    <label htmlFor="status">Filtrar por Estado</label>
                     <div className={styles.inputWrapper}>
                        <FilterIcon />
                        <select id="status" value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
                            <option value="all">Todos los Estados</option>
                            <option value="Confirmado">Confirmado</option>
                            <option value="Pendiente">Pendiente</option>
                            <option value="Cancelado">Cancelado</option>
                        </select>
                    </div>
                </div>
            </div>
            
            <div className={styles.tableContainer}>
                {/* ... (código de la tabla sin cambios) ... */}
            </div>
            
            <ReservaModal show={isModalOpen} onClose={handleCloseModal} onSave={handleSaveReserva} reserva={editingReserva} />
            <ConfirmationModal 
                show={showDeleteModal}
                title="Confirmar Eliminación"
                message="¿Estás seguro de que quieres eliminar esta reserva de forma permanente?"
                onConfirm={confirmDelete}
                onCancel={() => setShowDeleteModal(false)}
            />
        </div>
    );
};

// Pega aquí las funciones que no cambiaron para tener el archivo completo
// fetchReservas, handleDeleteClick, confirmDelete, handleOpenModal, handleCloseModal, handleSaveReserva
// y el return completo de la tabla

export default ManageReservasPage;
