// src/pages/ReservationsPage.jsx
import React, { useState, useContext, useEffect } from 'react';
import { createReservation } from '../services/reservasService';
import AuthContext from '../context/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';
import DateRangePicker from '../components/reservations/DateRangePicker'; // <-- 1. IMPORTAMOS EL NUEVO COMPONENTE
import styles from './ReservationsPage.module.css';

const ReservationsPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    
    // Leemos la habitación preseleccionada
    const preselectedRoom = location.state?.preselectedRoom;

    // Estado unificado para las fechas
    const [fechas, setFechas] = useState({ inicio: '', fin: '' });
    
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { user } = useContext(AuthContext);

    // Si no hay una habitación preseleccionada, redirige a la página de habitaciones
    useEffect(() => {
        if (!preselectedRoom) {
            navigate('/habitaciones');
        }
    }, [preselectedRoom, navigate]);

    const handleConfirm = async () => {
        if (!user) {
            navigate('/login', { state: { from: location } });
            return;
        }
        if (!fechas.inicio || !fechas.fin) {
            setError('Por favor, selecciona las fechas de tu estadía.');
            return;
        }

        setLoading(true);
        setError('');
        try {
            const reservationData = {
                habitacion_id: preselectedRoom._id,
                fecha_inicio: fechas.inicio,
                fecha_fin: fechas.fin
            };
            await createReservation(reservationData);
            // Redirigir a una página de éxito (a implementar) o mostrar un mensaje
            alert('¡Reserva Exitosa! Hemos enviado una confirmación a tu correo.');
            navigate('/');
        } catch (err) {
            setError(err.response?.data?.message || 'No se pudo crear la reserva. La habitación podría no estar disponible para esas fechas.');
        }
        setLoading(false);
    };

    // Calculamos los días y el total solo si tenemos todos los datos
    const diasEstadia = fechas.inicio && fechas.fin ? 
        Math.max(1, (new Date(fechas.fin) - new Date(fechas.inicio)) / (1000 * 60 * 60 * 24))
        : 0;
    const precioTotal = diasEstadia * (preselectedRoom?.precio_noche || 0);

    // Si no hay habitación, no renderizamos nada hasta la redirección
    if (!preselectedRoom) {
        return <div className="container"><p>Cargando...</p></div>;
    }

    return (
        <div className="container">
            <h1>Reservar: {preselectedRoom.nombre}</h1>

            <div className="card">
                <h2>Confirma tu estadía</h2>
                <p>Selecciona las fechas de llegada y salida para tu visita.</p>
                
                {/* --- 2. USAMOS EL NUEVO COMPONENTE --- */}
                <DateRangePicker fechas={fechas} onDateChange={setFechas} />
                
                {error && <p className={styles.errorMessage}>{error}</p>}
                
                <div className={styles.confirmationDetails}>
                    <h3>Detalles de tu elección</h3>
                    <p><strong>Habitación:</strong> {preselectedRoom.nombre}</p>
                    <p><strong>Descripción:</strong> {preselectedRoom.descripcion}</p>
                    
                    {fechas.inicio && fechas.fin && (
                        <>
                            <h3>Resumen de Costo</h3>
                            <p><strong>Duración:</strong> {diasEstadia} {diasEstadia > 1 ? 'noches' : 'noche'}</p>
                            <h3 className={styles.totalPrice}>Total a Pagar: ${precioTotal.toFixed(0)}</h3>
                        </>
                    )}
                </div>

                <div className={styles.buttonContainer}>
                    <button onClick={() => navigate('/habitaciones')} className={styles.backButton}>
                        Cambiar Habitación
                    </button>
                    <button onClick={handleConfirm} disabled={loading || !fechas.inicio || !fechas.fin}>
                        {loading ? 'Confirmando...' : 'Confirmar Reserva'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ReservationsPage;
