// src/pages/ManageReservasPage.jsx
import React, { useState, useEffect, useMemo } from 'react';
import { getReservas, deleteReserva } from '../services/adminService';
import styles from './ManageReservasPage.module.css';

const ManageReservasPage = () => {
    const [reservas, setReservas] = useState([]);
    const [filteredReservas, setFilteredReservas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- Estados para los filtros ---
    const [searchTerm, setSearchTerm] = useState('');
    const [categoryFilter, setCategoryFilter] = useState('all');
    const [costFilter, setCostFilter] = useState('');

    useEffect(() => {
        fetchReservas();
    }, []);

    const fetchReservas = async () => {
        try {
            setLoading(true);
            const response = await getReservas();
            setReservas(response.data);
            setFilteredReservas(response.data); // Inicialmente, no hay filtros
        } catch (err) {
            setError('No se pudieron cargar las reservas.');
        } finally {
            setLoading(false);
        }
    };
    
    // --- Lógica de filtrado ---
    // useMemo optimiza el rendimiento para no recalcular en cada render
    useEffect(() => {
        let result = reservas;

        // Búsqueda por ID
        if (searchTerm) {
            result = result.filter(r => r._id.toLowerCase().includes(searchTerm.toLowerCase()));
        }
        
        // Filtro por categoría (nombre de habitación)
        if (categoryFilter !== 'all') {
             result = result.filter(r => r.habitacion_id?.nombre === categoryFilter);
        }

        // Filtro por costo
        if (costFilter) {
            result = result.filter(r => r.precio_final >= Number(costFilter));
        }

        setFilteredReservas(result);
    }, [searchTerm, categoryFilter, costFilter, reservas]);


    const handleDelete = async (id) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar esta reserva?')) {
            try {
                await deleteReserva(id);
                fetchReservas(); // Recargar la lista después de eliminar
            } catch (err) {
                alert('No se pudo eliminar la reserva.');
            }
        }
    };

    const formatDate = (dateString) => new Date(dateString).toLocaleDateString();
    
    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Reservas</h1>
                <button className={styles.addButton}>Crear Nueva Reserva</button>
            </div>

            {/* --- Barra de Filtros --- */}
            <div className={`card ${styles.filtersCard}`}>
                <input
                    type="text"
                    placeholder="Buscar por ID de reserva..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <select value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}>
                    <option value="all">Todas las Categorías</option>
                    <option value="Doble Clásica">Doble Clásica</option>
                    <option value="Matrimonial Superior">Matrimonial Superior</option>
                    <option value="Individual Ejecutiva">Individual Ejecutiva</option>
                </select>
                <input
                    type="number"
                    placeholder="Filtrar por costo mínimo..."
                    value={costFilter}
                    onChange={(e) => setCostFilter(e.target.value)}
                />
            </div>
            
            {/* --- Tabla de Reservas --- */}
            <div className={styles.tableContainer}>
                {loading && <p>Cargando...</p>}
                {error && <p className="error-message">{error}</p>}
                {!loading && (
                    <table>
                        <thead>
                            <tr>
                                <th>ID Reserva</th>
                                <th>Cliente</th>
                                <th>Email</th>
                                <th>Habitación</th>
                                <th>Check-in</th>
                                <th>Check-out</th>
                                <th>Costo Total</th>
                                <th>Estado</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredReservas.length > 0 ? filteredReservas.map(reserva => (
                                <tr key={reserva._id}>
                                    <td data-label="ID Reserva">{reserva._id}</td>
                                    <td data-label="Cliente">{reserva.cliente_id?.nombre}</td>
                                    <td data-label="Email">{reserva.cliente_id?.email}</td>
                                    <td data-label="Habitación">{reserva.habitacion_id?.nombre}</td>
                                    <td data-label="Check-in">{formatDate(reserva.fecha_inicio)}</td>
                                    <td data-label="Check-out">{formatDate(reserva.fecha_fin)}</td>
                                    <td data-label="Costo Total">${reserva.precio_final.toFixed(0)}</td>
                                    <td data-label="Estado"><span className={`${styles.status} ${styles[`status_${reserva.estado.toLowerCase()}`]}`}>{reserva.estado}</span></td>
                                    <td data-label="Acciones">
                                        <button className={styles.actionButton}>Editar</button>
                                        <button onClick={() => handleDelete(reserva._id)} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                    </td>
                                </tr>
                            )) : (
                                <tr>
                                    <td colSpan="9">No se encontraron reservas con los filtros actuales.</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default ManageReservasPage;
