// src/pages/HabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAllHabitaciones } from '../services/habitacionesService';
import styles from './HabitacionesPage.module.css';

// --- Componente para la Tarjeta de Resumen de Habitación ---
const RoomSummaryCard = ({ habitacion, onViewDetails }) => {
    const roomImage = `https://placehold.co/600x400/005f73/ffffff?text=${habitacion.nombre.replace(' ', '+')}`;
    return (
        <div className={`${styles.summaryCard} ${!habitacion.disponible ? styles.notAvailableSummary : ''}`}>
            <div className={styles.summaryImageContainer}>
                <img src={roomImage} alt={habitacion.nombre} />
                {!habitacion.disponible && <div className={styles.notAvailableBadge}>Reservado Hoy</div>}
            </div>
            <div className={styles.summaryInfo}>
                <h3>{habitacion.nombre}</h3>
                <p className={styles.summaryPrice}>${(habitacion.precio || 0).toLocaleString('es-CL')} / noche</p>
                <button onClick={() => onViewDetails(habitacion)} className={styles.detailsButton}>
                    Ver Detalles
                </button>
            </div>
        </div>
    );
};

// --- Componente para el Modal con los Detalles Completos ---
const RoomDetailModal = ({ habitacion, onClose }) => {
    // ... (Este componente interno no necesita cambios)
};

// --- Componente Principal de la Página ---
const HabitacionesPage = () => {
    const [habitaciones, setHabitaciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedRoom, setSelectedRoom] = useState(null);

    // --- Estado unificado para todos los filtros ---
    const [filters, setFilters] = useState({
        searchTerm: '',
        disponibilidad: 'true',
        ciudad: '',
        sort: 'precio-asc'
    });
    
    const ciudadesDisponibles = ['Santiago', 'Viña del Mar']; // A futuro, esto puede venir de la API de Hoteles

    // Se ejecuta cada vez que el objeto de filtros cambia
    useEffect(() => {
        const fetchHabitaciones = async () => {
            setLoading(true);
            try {
                // Limpiamos los filtros vacíos antes de enviar
                const activeFilters = Object.fromEntries(
                    Object.entries(filters).filter(([_, v]) => v !== '' && v !== 'todas')
                );
                const result = await getAllHabitaciones(activeFilters);
                setHabitaciones(result.data || []);
                setError('');
            } catch (err) {
                setError('No se pudieron cargar las habitaciones.');
            } finally {
                setLoading(false);
            }
        };

        const timer = setTimeout(() => {
            fetchHabitaciones();
        }, 300); // Pequeño delay para no hacer una petición en cada tecla

        return () => clearTimeout(timer);
    }, [filters]);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({ ...prev, [name]: value }));
    };

    const renderContent = () => {
        if (loading) return <p className={styles.loadingText}>Cargando habitaciones...</p>;
        if (error) return <p className={styles.errorText}>{error}</p>;
        if (habitaciones.length > 0) {
            return habitaciones.map(h => <RoomSummaryCard key={h._id} habitacion={h} onViewDetails={setSelectedRoom} />);
        }
        return <p className={styles.noResults}>No se encontraron habitaciones que coincidan con tus criterios.</p>;
    };

    return (
        <>
            <div className="container">
                <div className={styles.header}>
                    <h1>Nuestras Habitaciones</h1>
                    <p>Encuentra el espacio perfecto diseñado para tu confort y descanso.</p>
                </div>

                <div className={`card ${styles.filtersContainer}`}>
                    <input
                        type="text"
                        name="searchTerm"
                        placeholder="Buscar por habitación o ciudad..."
                        className={styles.searchInput}
                        value={filters.searchTerm}
                        onChange={handleFilterChange}
                    />
                    <select name="disponibilidad" value={filters.disponibilidad} onChange={handleFilterChange}>
                        <option value="true">Mostrar solo disponibles</option>
                        <option value="">Mostrar todas</option>
                    </select>
                    <select name="ciudad" value={filters.ciudad} onChange={handleFilterChange}>
                        <option value="">Todas las ciudades</option>
                        {ciudadesDisponibles.map(c => <option key={c} value={c}>{c}</option>)}
                    </select>
                    <select name="sort" value={filters.sort} onChange={handleFilterChange}>
                        <option value="precio-asc">Ordenar por Precio</option>
                        <option value="categoria">Ordenar por Categoría</option>
                    </select>
                </div>
                
                <div className={styles.roomList}>
                    {renderContent()}
                </div>
            </div>
            
            {selectedRoom && <RoomDetailModal habitacion={selectedRoom} onClose={() => setSelectedRoom(null)} />}
        </>
    );
};

export default HabitacionesPage;
