// src/pages/AdminDashboardPage.jsx
import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import { getReporteGeneral } from '../services/adminService';
import styles from './AdminDashboardPage.module.css';

// Pequeños componentes de íconos para la UI
const StatIcon = ({ children }) => <div className={styles.statIcon}>{children}</div>;
const ArrowIcon = () => <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5"><polyline points="9 18 15 12 9 6"></polyline></svg>;

const AdminDashboardPage = () => {
    const { user } = useContext(AuthContext);
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchReport = async () => {
            try {
                const response = await getReporteGeneral();
                setReportData(response.data);
            } catch (error) {
                console.error("No se pudo cargar el reporte para el dashboard:", error);
                // Si falla, el dashboard seguirá funcionando sin las estadísticas
                setReportData({ ocupacion: 0, ingresosMesActual: 0, nuevasReservasMes: 0 });
            } finally {
                setLoading(false);
            }
        };

        fetchReport();
    }, []);

    return (
        <div className={`${styles.dashboardContainer} container`}>
            <div className={styles.header}>
                <h1>Panel de Administración</h1>
                <p>Bienvenido, {user?.nombre}. Desde aquí puedes gestionar las operaciones del hotel.</p>
            </div>

            {/* --- SECCIÓN DE MÉTRICAS CLAVE --- */}
            <div className={styles.statsGrid}>
                <div className="card">
                    <span className={styles.statValue}>{loading ? '...' : `${reportData?.ocupacion}%`}</span>
                    <span className={styles.statLabel}>Ocupación Hoy</span>
                </div>
                <div className="card">
                    <span className={styles.statValue}>{loading ? '...' : `$${reportData?.ingresosMesActual.toLocaleString('es-CL')}`}</span>
                    <span className={styles.statLabel}>Ingresos del Mes</span>
                </div>
                <div className="card">
                    <span className={styles.statValue}>{loading ? '...' : reportData?.nuevasReservasMes}</span>
                    <span className={styles.statLabel}>Nuevas Reservas (Mes)</span>
                </div>
                <Link to="/admin/reportes" className={`${styles.reportLinkCard} card`}>
                    <span className={styles.statValue}>Ver Reportes</span>
                    <span className={styles.statLabel}>Analizar todas las métricas</span>
                </Link>
            </div>

            {/* --- SECCIÓN DE GESTIÓN --- */}
            <div className={styles.managementSection}>
                <h2>Módulos de Gestión</h2>
                <div className={styles.managementGrid}>
                    <Link to="/admin/reservas" className={`${styles.managementCard} card`}>
                        <div>
                            <h3>Gestionar Reservas</h3>
                            <p>Ver, modificar y cancelar las reservas de los clientes.</p>
                        </div>
                        <ArrowIcon />
                    </Link>
                    <Link to="/admin/habitaciones" className={`${styles.managementCard} card`}>
                        <div>
                            <h3>Gestionar Habitaciones</h3>
                            <p>Añadir nuevas habitaciones, editar precios y disponibilidad.</p>
                        </div>
                        <ArrowIcon />
                    </Link>
                     <Link to="/admin/usuarios" className={`${styles.managementCard} card`}>
                        <div>
                            <h3>Gestionar Usuarios</h3>
                            <p>Ver, editar y gestionar los roles de los usuarios registrados.</p>
                        </div>
                        <ArrowIcon />
                    </Link>
                     <Link to="/admin/hoteles" className={`${styles.managementCard} card`}>
                        <div>
                            <h3>Gestionar Hoteles</h3>
                            <p>Añadir nuevos hoteles y configurar sus servicios.</p>
                        </div>
                        <ArrowIcon />
                    </Link>
                     <Link to="/admin/servicios" className={`${styles.managementCard} card`}>
                        <div>
                            <h3>Gestionar Servicios</h3>
                            <p>Definir los servicios adicionales que cada hotel ofrece.</p>
                        </div>
                        <ArrowIcon />
                    </Link>
                     <Link to="#" className={`${styles.managementCard} card`}>
                        <div>
                            <h3>Gestionar Promociones</h3>
                            <p>Lanzar nuevas ofertas y descuentos para atraer clientes.</p>
                        </div>
                        <ArrowIcon />
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboardPage;
