// src/components/admin/UserModal.jsx
import React, { useState, useEffect } from 'react';
import styles from './ReservaModal.module.css'; // Reutilizamos estilos

const UserModal = ({ show, onClose, onSave, user }) => {
    const [formData, setFormData] = useState({
        _id: '',
        nombre: '',
        email: '',
        password: '',
        rol: 'cliente',
    });

    useEffect(() => {
        if (user) {
            // Lógica para modo "Editar" (a futuro)
            setFormData({
                _id: user._id || '',
                nombre: user.nombre || '',
                email: user.email || '',
                password: '', // La contraseña no se precarga por seguridad
                rol: user.rol || 'cliente',
            });
        } else {
            // Lógica para modo "Crear"
            setFormData({
                _id: '',
                nombre: '',
                email: '',
                password: '',
                rol: 'cliente',
            });
        }
    }, [user, show]);


    if (!show) {
        return null;
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData);
    };

    return (
        <div className={styles.modalOverlay}>
            <div className={`${styles.modalContent} ${styles.smallModal}`}>
                <h2>{user ? 'Editar Usuario' : 'Crear Nuevo Usuario'}</h2>
                <form onSubmit={handleSubmit}>
                    <div className={styles.inputGroup}>
                        <label>RUN / RUT</label>
                        <input type="text" name="_id" value={formData._id} onChange={handleChange} placeholder="12345678-9" required disabled={!!user} />
                    </div>
                    <div className={styles.inputGroup}>
                        <label>Nombre Completo</label>
                        <input type="text" name="nombre" value={formData.nombre} onChange={handleChange}placeholder="Ej: Juan Pérez" required />
                    </div>
                     <div className={styles.inputGroup}>
                        <label>Correo Electrnico</label>
                        <input type="email" name="email" value={formData.email} onChange={handleChange}placeholder="Ej: juanperez@gmail.com" required />
                    </div>
                     <div className={styles.inputGroup}>
                        <label>Contraseña Temporal</label>
                        <input type="password" name="password" value={formData.password} onChange={handleChange}placeholder="******" required={!user} minLength="6" />
                    </div>
                     <div className={styles.inputGroup}>
                        <label>Rol</label>
                        <select name="rol" value={formData.rol} onChange={handleChange}>
                            <option value="cliente">Cliente</option>
                            <option value="gerente">Gerente</option>
                            <option value="admin">Admin</option>
                        </select>
                    </div>

                    <div className={styles.buttonGroup}>
                        <button type="button" onClick={onClose} className={styles.cancelButton}>Cancelar</button>
                        <button type="submit">Guardar Usuario</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UserModal;
