import React, { useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import styles from './Navbar.module.css';

import { useAuth } from '../../context/AuthContext';

const Navbar = () => {
    const { user, logout } = useAuth();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <nav className={styles.navbar}>
            <div className={`container ${styles.navContainer}`}>
                <Link to="/" className={styles.navLogo}>VERANUM</Link>

                <div className={`${styles.navMenu} ${isMenuOpen ? styles.showMenu : ''}`}>
                    <NavLink to="/habitaciones" className={({ isActive }) => isActive ? `${styles.navItem} ${styles.active}` : styles.navItem}>Habitaciones</NavLink>
                    <NavLink to="/servicios" className={({ isActive }) => isActive ? `${styles.navItem} ${styles.active}` : styles.navItem}>Servicios</NavLink>
                    
                    {/* --- ENLACE "NOSOTROS" SIMPLIFICADO --- */}
                    <NavLink 
                      to="/nosotros" 
                      className={({ isActive }) => isActive ? `${styles.navItem} ${styles.active}` : styles.navItem}
                    >
                      Nosotros
                    </NavLink>

                    {user && (
                        <NavLink to="/dashboard" className={({ isActive }) => isActive ? `${styles.navItem} ${styles.active}` : styles.navItem}>Dashboard</NavLink>
                    )}
                </div>

                <div className={styles.navActions}>
                    {user ? (
                        <>
                            <span className={styles.userName}>Administrador {user.nombre}</span>
                            <button onClick={logout} className={styles.logoutButton}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path><polyline points="16 17 21 12 16 7"></polyline><line x1="21" y1="12" x2="9" y2="12"></line></svg>
                            </button>
                        </>
                    ) : (
                        <Link to="/login" className={styles.loginLink}>Iniciar Sesi√≥n</Link>
                    )}
                    <div className={styles.menuToggle} onClick={toggleMenu}>
                        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><line x1="3" y1="12" x2="21" y2="12"></line><line x1="3" y1="6" x2="21" y2="6"></line><line x1="3" y1="18" x2="21" y2="18"></line></svg>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
