// src/App.jsx
import React from 'react';
import { Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ReservationsPage from './pages/ReservationsPage';
import AdminDashboardPage from './pages/AdminDashboardPage';
// --- 1. IMPORTA LAS NUEVAS PÁGINAS DE HABITACIONES ---
import RoomDoblePage from './pages/RoomDoblePage';
import RoomMatrimonialPage from './pages/RoomMatrimonialPage';
import RoomIndividualPage from './pages/RoomIndividualPage';

import Navbar from './components/layout/Navbar';
import Footer from './components/layout/Footer';
import { ReservationProvider } from './context/ReservationContext';

function App() {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Navbar />
      <main style={{ flex: 1, backgroundColor: 'var(--light-gray-color)' }}>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route 
            path="/reservar" 
            element={
              <ReservationProvider>
                <ReservationsPage />
              </ReservationProvider>
            } 
          />
          <Route path="/admin" element={<AdminDashboardPage />} />

          {/* --- 2. AÑADE LAS NUEVAS RUTAS --- */}
          <Route path="/habitacion/doble" element={<RoomDoblePage />} />
          <Route path="/habitacion/matrimonial" element={<RoomMatrimonialPage />} />
          <Route path="/habitacion/individual" element={<RoomIndividualPage />} />

        </Routes>
      </main>
      <Footer />
    </div>
  );
}

export default App;
