// src/services/habitacionesService.js
import api from './api';


// Funci칩n para obtener TODAS las habitaciones desde la API, ahora con filtros
export const getAllHabitaciones = async (params) => {
  // axios se encargar치 de convertir el objeto 'params' en una query string
  // ej: /habitaciones?search=suite&ciudad=Santiago
  const response = await api.get('/habitaciones', { params });
  return response.data;
};
