// src/components/reservations/DateRangePicker.jsx
import React from 'react';
import styles from './DateRangePicker.module.css';

const DateRangePicker = ({ fechas, onDateChange }) => {
    // Obtenemos la fecha de hoy en formato YYYY-MM-DD
    const hoy = new Date().toISOString().split('T')[0];
    
    // Calculamos el día siguiente al check-in para el mínimo del check-out
    const diaSiguienteAlCheckIn = fechas.inicio ? new Date(new Date(fechas.inicio).getTime() + 86400000).toISOString().split('T')[0] : hoy;

    const handleDateChange = (e) => {
        const { name, value } = e.target;
        const nuevasFechas = { ...fechas, [name]: value };

        // Si la nueva fecha de check-in es posterior a la de check-out, se resetea la de check-out
        if (name === 'inicio' && nuevasFechas.fin && new Date(value) >= new Date(nuevasFechas.fin)) {
            nuevasFechas.fin = '';
        }
        onDateChange(nuevasFechas);
    };

    return (
        <div className={styles.datePickerContainer}>
            <div className={styles.dateInputGroup}>
                <label htmlFor="check-in">Check-in</label>
                <input 
                    type="date" 
                    id="check-in" 
                    name="inicio"
                    value={fechas.inicio}
                    onChange={handleDateChange}
                    min={hoy} // El día mínimo para seleccionar es hoy
                />
            </div>
            <div className={styles.arrow}>→</div>
            <div className={styles.dateInputGroup}>
                <label htmlFor="check-out">Check-out</label>
                <input 
                    type="date" 
                    id="check-out"
                    name="fin"
                    value={fechas.fin}
                    onChange={handleDateChange}
                    min={diaSiguienteAlCheckIn} // El mínimo es el día después del check-in
                    disabled={!fechas.inicio} // Se deshabilita hasta que se elija el check-in
                />
            </div>
        </div>
    );
};

export default DateRangePicker;
