// src/pages/HabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAllHabitaciones } from '../services/habitacionesService';
import styles from './HabitacionesPage.module.css'; // Usaremos estilos dedicados

const HabitacionesPage = () => {
  const [habitaciones, setHabitaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHabitaciones = async () => {
      try {
        const result = await getAllHabitaciones();
        setHabitaciones(result.data);
      } catch (err) {
        setError('No se pudieron cargar las habitaciones.');
      } finally {
        setLoading(false);
      }
    };

    fetchHabitaciones();
  }, []);

  if (loading) {
    return <div className="container"><p>Cargando habitaciones...</p></div>;
  }

  if (error) {
    return <div className="container"><p style={{color: 'red'}}>{error}</p></div>;
  }

  const getRoomLink = (nombre) => {
    switch (nombre) {
      case 'Doble Clásica':
        return '/habitacion/doble';
      case 'Matrimonial Superior':
        return '/habitacion/matrimonial';
      case 'Individual Ejecutiva':
        return '/habitacion/individual';
      default:
        return '#'; // Enlace por defecto si no coincide
    }
  };

  return (
    <div className="container">
      <div className={styles.header}>
        <h1>Nuestras Habitaciones</h1>
        <p>Encuentra el espacio perfecto diseñado para tu confort y descanso.</p>
      </div>
      <div className={styles.roomGrid}>
        {habitaciones.map(h => (
          <div key={h._id} className={styles.roomCard}>
            <div className={styles.roomImage}>
                {/* Usamos un placeholder genérico para la galería */}
                <img src={`https://placehold.co/600x400/0d47a1/ffffff?text=${h.nombre.replace(' ', '+')}`} alt={h.nombre} />
            </div>
            <div className={styles.roomInfo}>
                <h3>{h.nombre}</h3>
                <p className={styles.roomDescription}>{h.descripcion.substring(0, 100)}...</p>
                <div className={styles.roomPrice}>Desde ${h.precio_noche.toFixed(0)}/noche</div>
                <Link to={getRoomLink(h.nombre)}>
                    <button>Ver Más Detalles</button>
                </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HabitacionesPage;
