// src/components/layout/Navbar.jsx
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import styles from './Navbar.module.css'; // Usaremos módulos de CSS para evitar conflictos

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className={styles.navbar}>
      <div className={styles.navContainer}>
        <Link to="/" className={styles.navLogo}>VERANUM</Link>
        <div className={styles.navMenu}>
          <Link to="/reservar" className={styles.navLink}>Reservar</Link>
          {user && (user.rol === 'admin' || user.rol === 'gerente') && (
            <Link to="/admin" className={styles.navLink}>Admin</Link>
          )}
          {user ? (
            <>
              <span className={styles.navUser}>Hola, {user.nombre}</span>
              <button onClick={handleLogout} className={styles.navButton}>Salir</button>
            </>
          ) : (
            <Link to="/login" className={styles.navLink}>Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
