import React, { useState, useEffect, useContext } from 'react';
import { useLocation, Link, useNavigate } from 'react-router-dom';
import styles from './ReservationsPage.module.css'; // Usando tu archivo de estilos
import AuthContext from '../context/AuthContext';
import { createReservation } from '../services/reservasService';
import Notification from '../components/common/Notification';

// La función para obtener la imagen se mantiene
const getRoomImageUrl = (roomName) => {
    const name = (roomName || '').toLowerCase();
    if (name.includes('doble')) {
        return 'https://images-new.pxsol.com/0yXQIHHgg6QPIqHMdt5cFppRjSdRImPYVZKA8Zjp0F0/rs:fill:600:400:1/q:80/plain/https://files-public-web.s3-us-west-2.amazonaws.com/1217/company/library/user/209394497137de94aa9d4ab3e8a7f395b32600345c1.jpeg@jpeg';
    } else if (name.includes('individual')) {
        return 'https://www.abbahoteles.com/idb/72372/hotel_balmoral_individual-600x400.jpg';
    } else if (name.includes('matrimonial')) {
        return 'https://www.hotelterrano.cl/wp-content/uploads/2021/10/suite-1.jpg';
    } else {
        return `https://placehold.co/600x400/005f73/ffffff?text=${(roomName || '').replace(' ', '+')}`;
    }
};

// Se asume una lista de servicios estática o que vendrá de otro lado
const serviciosAdicionales = [
    { _id: 'servicio1', nombre: 'Uso del Gimnasio', precio_diario: 7000 },
    { _id: 'servicio2', nombre: 'Acceso a Piscina', precio_diario: 4000 },
    { _id: 'servicio3', nombre: 'Acceso a Spa', precio_diario: 20000 },
    { _id: 'servicio4', nombre: 'Arriendo Cancha de Tenis', precio_diario: 7000 },
];

const ReservationsPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const { preselectedRoom } = location.state || {};

    // Estados existentes en tu archivo
    const [checkIn, setCheckIn] = useState('');
    const [checkOut, setCheckOut] = useState('');
    const [noches, setNoches] = useState(0);
    const [serviciosSeleccionados, setServiciosSeleccionados] = useState(new Set());
    const [costoAlojamiento, setCostoAlojamiento] = useState(0);
    const [costoServicios, setCostoServicios] = useState(0);
    const [costoTotal, setCostoTotal] = useState(0);

    // Estados añadidos para la funcionalidad del botón
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [notification, setNotification] = useState({ show: false, message: '', type: '' });

    useEffect(() => {
        if (checkIn && checkOut) {
            const date1 = new Date(checkIn);
            const date2 = new Date(checkOut);
            const diffTime = date2 - date1;
            if (diffTime > 0) {
                const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                setNoches(diffDays);
                setCostoAlojamiento(diffDays * (preselectedRoom?.precio || 0));
            } else {
                setNoches(0);
                setCostoAlojamiento(0);
            }
        }
    }, [checkIn, checkOut, preselectedRoom]);

    useEffect(() => {
        let totalServicios = 0;
        serviciosSeleccionados.forEach(servicioId => {
            const servicio = serviciosAdicionales.find(s => s._id === servicioId);
            if (servicio) totalServicios += servicio.precio_diario;
        });
        setCostoServicios(totalServicios * noches);
    }, [serviciosSeleccionados, noches]);

    useEffect(() => {
        setCostoTotal(costoAlojamiento + costoServicios);
    }, [costoAlojamiento, costoServicios]);

    const handleServicioChange = (id) => {
        const nuevosServicios = new Set(serviciosSeleccionados);
        nuevosServicios.has(id) ? nuevosServicios.delete(id) : nuevosServicios.add(id);
        setServiciosSeleccionados(nuevosServicios);
    };

    // --- LÓGICA AÑADIDA PARA EL BOTÓN ---
    const handleConfirm = async () => {
        if (!user) {
            navigate('/login', { state: { from: location, message: 'Debes iniciar sesión para reservar.' } });
            return;
        }
        if (noches <= 0) {
            setError('Por favor, selecciona un rango de fechas válido.');
            return;
        }
        setLoading(true);
        setError('');
        try {
            const reservationData = {
                habitacion_id: preselectedRoom._id,
                hotel_id: preselectedRoom.hotel_id,
                fecha_inicio: checkIn,
                fecha_fin: checkOut,
                estado: 'Confirmado',
                servicios_adicionales: Array.from(serviciosSeleccionados)
            };
            await createReservation(reservationData);
            setNotification({ show: true, message: '¡Reserva Exitosa! Serás redirigido.', type: 'success' });
            setTimeout(() => navigate('/dashboard/mis-reservas'), 2500);
        } catch (err) {
            setError(err.response?.data?.message || 'No se pudo procesar la reserva.');
        } finally {
            setLoading(false);
        }
    };

    if (!preselectedRoom) {
        return (
            <div className="container" style={{ textAlign: 'center', padding: '4rem 0' }}>
                <h1>Error</h1>
                <p>No se ha seleccionado ninguna habitación. Por favor, <Link to="/habitaciones">vuelve a la página de habitaciones</Link> y elige una.</p>
            </div>
        );
    }

    const roomImage = getRoomImageUrl(preselectedRoom.nombre);

    return (
        <>
            <Notification show={notification.show} message={notification.message} type={notification.type} onClose={() => setNotification({ show: false, message: '', type: '' })} />
            <div className={`container ${styles.reservationWrapper}`}>
                <div className={styles.roomSummary}>
                    <img src={roomImage} alt={preselectedRoom.nombre} />
                    <h2>{preselectedRoom.nombre}</h2>
                    <p>{preselectedRoom.descripcion}</p>
                    {/* Este es un ejemplo, puedes añadir más detalles si los tienes */}
                    <p><strong>Capacidad:</strong> {preselectedRoom.capacidad} personas</p>
                </div>

                <div className={styles.bookingForm}>
                    <h3>Completa tu reserva</h3>
                    <p className={styles.formSubtitle}>Selecciona las fechas de tu estadía.</p>
                    
                    {/* Tu estructura original para fechas y servicios */}
                    <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
                        <input type="date" value={checkIn} onChange={e => setCheckIn(e.target.value)} />
                        <span>→</span>
                        <input type="date" value={checkOut} onChange={e => setCheckOut(e.target.value)} />
                    </div>

                    <div style={{ marginTop: '2rem' }}>
                        <h4>Añade Servicios Adicionales (costo por día)</h4>
                        {serviciosAdicionales.map(servicio => (
                            <div key={servicio._id}>
                                <label>
                                    <input type="checkbox" checked={serviciosSeleccionados.has(servicio._id)} onChange={() => handleServicioChange(servicio._id)} />
                                    {servicio.nombre} +${servicio.precio_diario.toLocaleString('es-CL')}
                                </label>
                            </div>
                        ))}
                    </div>

                    {noches > 0 && (
                        <div className={styles.summary}>
                            <div className={styles.summaryLine}>
                                <span>{preselectedRoom.precio.toLocaleString('es-CL')} x {noches} noches</span>
                                <span>${costoAlojamiento.toLocaleString('es-CL')}</span>
                            </div>
                            {costoServicios > 0 && (
                                <div className={styles.summaryLine}>
                                    <span>Servicios adicionales</span>
                                    <span>${costoServicios.toLocaleString('es-CL')}</span>
                                </div>
                            )}
                            <div className={styles.summaryLine_total}>
                                <span>Total</span>
                                <span>${costoTotal.toLocaleString('es-CL')}</span>
                            </div>
                        </div>
                    )}

                    {error && <p className={styles.errorMessage}>{error}</p>}
                    
                    <div className={styles.buttonContainer}>
                        {/* BOTÓN AHORA FUNCIONAL */}
                        <button onClick={handleConfirm} disabled={loading || noches <= 0}>
                            {loading ? 'Procesando...' : 'Confirmar y Reservar'}
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
};

export default ReservationsPage;
