// src/services/adminService.js
import api from './api';

// --- NUEVA SECCIÓN PARA GESTIÓN DE USUARIOS ---
export const getAllUsers = async () => {
    const response = await api.get('/usuarios/admin/all');
    return response.data;
};

// --- RESERVATIONS MANAGEMENT ---
export const getReservas = async () => {
  const response = await api.get('/reservas/admin/all');
  return response.data;
};
// ... (resto de tus funciones existentes de reservas) ...


// --- HABITACIONES MANAGEMENT ---
export const createHabitacion = async (data) => {
    const response = await api.post('/habitaciones', data);
    return response.data;
};
// ... (resto de tus funciones existentes de habitaciones) ...


// --- REPORTES ---
export const getReporteGeneral = async () => {
    const response = await api.get('/reportes/general');
    return response.data;
};
