// src/services/habitacionesService.js
import api from './api';


// Funci칩n para obtener TODAS las habitaciones desde la API, ahora con filtros
export const getAllHabitaciones = async (filters = {}) => {
  // axios se encargar치 de convertir el objeto 'filters' en una query string
  // ej: /habitaciones?searchTerm=suite&ciudad=Santiago
  const response = await api.get('/habitaciones', { params: filters });
  return response.data;
};
