// src/pages/HabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAllHabitaciones } from '../services/habitacionesService';
import styles from './HabitacionesPage.module.css'; // Usaremos este archivo de estilos

const HabitacionesPage = () => {
  const [habitaciones, setHabitaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHabitaciones = async () => {
      try {
        const result = await getAllHabitaciones();
        setHabitaciones(result.data);
      } catch (err) {
        setError('No se pudieron cargar las habitaciones en este momento.');
      } finally {
        setLoading(false);
      }
    };

    fetchHabitaciones();
  }, []);

  const getRoomImage = (nombre) => {
    switch (nombre) {
      case 'Doble Clásica':
        return 'https://placehold.co/1200x600/1565c0/ffffff?text=Habitacion+Doble';
      case 'Matrimonial Superior':
        return 'https://placehold.co/1200x600/0d47a1/ffffff?text=Habitacion+Matrimonial';
      case 'Individual Ejecutiva':
        return 'https://placehold.co/1200x600/6c757d/ffffff?text=Habitacion+Individual';
      default:
        return `https://placehold.co/1200x600/333/fff?text=${nombre.replace(' ', '+')}`;
    }
  };

  if (loading) {
    return <div className="container"><p>Cargando habitaciones...</p></div>;
  }

  if (error) {
    return <div className="container"><p style={{color: 'red'}}>{error}</p></div>;
  }

  return (
    <div className="container">
      <div className={styles.header}>
        <h1>Nuestras Habitaciones</h1>
        <p>Encuentra el espacio perfecto diseñado para tu confort y descanso.</p>
      </div>

      {/* Mapeamos cada habitación a una tarjeta de detalle completa */}
      <div className={styles.roomList}>
        {habitaciones.map(h => (
          <div key={h._id} className={styles.roomDetailCard}>
            <div className={styles.roomImage}>
              <img src={getRoomImage(h.nombre)} alt={h.nombre} />
            </div>
            <div className={styles.roomInfo}>
              <h2>{h.nombre}</h2>
              <p className={styles.roomPrice}>${h.precio_noche.toFixed(0)} / noche</p>
              <p className={styles.roomDescription}>{h.descripcion}</p>
              
              <h3>Servicios Incluidos</h3>
              <ul className={styles.amenitiesList}>
                {h.servicios.map((servicio, index) => (
                    <li key={index}>{servicio}</li>
                ))}
              </ul>
              
              <Link to="/reservar">
                <button className={styles.reserveButton}>Reservar Esta Habitación</button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HabitacionesPage;
