// Función para obtener la imagen correcta (reutilizada)
const getRoomImageUrl = (roomName) => {
    const name = (roomName || '').toLowerCase();
    if (name.includes('doble')) return 'https://images-new.pxsol.com/0yXQIHHgg6QPIqHMdt5cFppRjSdRImPYVZKA8Zjp0F0/rs:fill:600:400:1/q:80/plain/https://files-public-web.s3-us-west-2.amazonaws.com/1217/company/library/user/209394497137de94aa9d4ab3e8a7f395b32600345c1.jpeg@jpeg';
    if (name.includes('individual')) return 'https://www.abbahoteles.com/idb/72372/hotel_balmoral_individual-600x400.jpg';
    if (name.includes('matrimonial')) return 'https://www.hotelterrano.cl/wp-content/uploads/2021/10/suite-1.jpg';
    return `https://placehold.co/600x400/005f73/ffffff?text=${(roomName || 'Habitación').replace(' ', '+')}`;
};

const ReservationsPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = useContext(AuthContext);

    const { preselectedRoom } = location.state || {};

    const [fechas, setFechas] = useState({ inicio: '', fin: '' });
    const [serviciosDisponibles, setServiciosDisponibles] = useState([]);
    const [serviciosSeleccionados, setServiciosSeleccionados] = useState(new Set());
    
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [notification, setNotification] = useState({ show: false, message: '', type: '' });

    useEffect(() => {
        if (!preselectedRoom) {
            navigate('/habitaciones');
        } else if (preselectedRoom.hotel_id) {
            getServiciosByHotelAdmin(preselectedRoom.hotel_id)
                .then(response => setServiciosDisponibles(response.data))
                .catch(err => console.error("Error al cargar servicios", err));
        }
    }, [preselectedRoom, navigate]);

    const handleServiceToggle = (servicioId) => {
        const nuevosServicios = new Set(serviciosSeleccionados);
        nuevosServicios.has(servicioId) ? nuevosServicios.delete(servicioId) : nuevosServicios.add(servicioId);
        setServiciosSeleccionados(nuevosServicios);
    };

    const { diasEstadia, costoAlojamiento, costoServicios, precioTotal } = useMemo(() => {
        if (!fechas.inicio || !fechas.fin || new Date(fechas.fin) <= new Date(fechas.inicio)) {
            return { diasEstadia: 0, costoAlojamiento: 0, costoServicios: 0, precioTotal: 0 };
        }

        const dias = Math.ceil((new Date(fechas.fin) - new Date(fechas.inicio)) / (1000 * 60 * 60 * 24));
        const costoBase = dias * (preselectedRoom?.precio || 0);
        const costoServiciosAdicionales = dias * serviciosDisponibles
            .filter(s => serviciosSeleccionados.has(s._id))
            .reduce((total, s) => total + s.precio_diario, 0);

        return { 
            diasEstadia: dias, 
            costoAlojamiento: costoBase, 
            costoServicios: costoServiciosAdicionales, 
            precioTotal: costoBase + costoServiciosAdicionales 
        };
    }, [fechas, preselectedRoom, serviciosSeleccionados, serviciosDisponibles]);

    // --- FUNCIÓN handleConfirm CORREGIDA ---
    const handleConfirm = async () => {
        if (!user) {
            navigate('/login', { state: { from: location } });
            return;
        }
        if (diasEstadia <= 0) {
            setError('Por favor, selecciona un rango de fechas válido.');
            return;
        }

        setLoading(true);
        setError('');
        try {
            const reservationData = {
                habitacion_id: preselectedRoom._id,
                hotel_id: preselectedRoom.hotel_id,
                fecha_inicio: fechas.inicio,
                fecha_fin: fechas.fin,
                estado: 'Confirmado',
                servicios_adicionales: Array.from(serviciosSeleccionados)
            };
            await createReservation(reservationData);
            
            setNotification({ show: true, message: '¡Reserva Exitosa! Serás redirigido.', type: 'success' });
            
            setTimeout(() => {
                navigate('/');
            }, 2500);

        } catch (err) {
            const errorMessage = err.response?.data?.message || 'No se pudo procesar la reserva. La habitación podría no estar disponible en esas fechas.';
            setError(errorMessage);
        } finally {
            // Se asegura de que el loading se detenga siempre
            setLoading(false); 
        }
    };

    if (!preselectedRoom) return <div className="container"><p>Redirigiendo...</p></div>;

    const roomImage = getRoomImageUrl(preselectedRoom.nombre);

    return (
        <>
            <Notification show={notification.show} message={notification.message} type={notification.type} onClose={() => setNotification({ show: false, message: '', type: '' })} />
            <div className={`container ${styles.bookingPageLayout}`}>
                <div className={styles.roomInfoColumn}>
                    <img src={roomImage} alt={preselectedRoom.nombre} className={styles.roomImage} />
                    <div className={styles.roomDetails}>
                        <h1>{preselectedRoom.nombre}</h1>
                        <p className={styles.roomDescription}>{preselectedRoom.descripcion}</p>
                        <div className={styles.roomCapacity}>
                            <span>{preselectedRoom.capacidad} personas</span>
                        </div>
                    </div>
                </div>

                <div className={styles.bookingFormColumn}>
                    <div className={styles.bookingCard}>
                        <h2>Completa tu reserva</h2>
                        <DateRangePicker onDateChange={setFechas} />
                    </div>

                    <div className={styles.bookingCard}>
                        <h3>Mejora tu estadía <span className={styles.costoPorDia}>(costo por día)</span></h3>
                        <div className={styles.servicesList}>
                            {serviciosDisponibles.map(servicio => (
                                <label key={servicio._id} className={styles.serviceItem}>
                                    <input type="checkbox" checked={serviciosSeleccionados.has(servicio._id)} onChange={() => handleServiceToggle(servicio._id)} />
                                    <span>{servicio.nombre}</span>
                                    <span className={styles.servicePrice}>+${servicio.precio_diario.toLocaleString('es-CL')}</span>
                                </label>
                            ))}
                        </div>
                    </div>

                    {diasEstadia > 0 && (
                        <div className={`${styles.bookingCard} ${styles.priceSummary}`}>
                            <h3>Resumen de tu estadía</h3>
                            <div className={styles.priceRow}>
                                <span>${preselectedRoom.precio.toLocaleString('es-CL')} x {diasEstadia} noches</span>
                                <span>${costoAlojamiento.toLocaleString('es-CL')}</span>
                            </div>
                            {costoServicios > 0 && (
                                <div className={styles.priceRow}>
                                    <span>Servicios adicionales</span>
                                    <span>${costoServicios.toLocaleString('es-CL')}</span>
                                </div>
                            )}
                            <hr className={styles.divider} />
                            <div className={`${styles.priceRow} ${styles.totalRow}`}>
                                <span>Total</span>
                                <span>${precioTotal.toLocaleString('es-CL')}</span>
                            </div>
                        </div>
                    )}
                    
                    {error && <p className={styles.errorMessage}>{error}</p>}
                    
                    <button className={styles.confirmButton} onClick={handleConfirm} disabled={loading || diasEstadia <= 0}>
                        {loading ? 'Procesando...' : 'Confirmar y Reservar'}
                    </button>
                </div>
            </div>
        </>
    );
};

export default ReservationsPage;
