// src/pages/HomePage.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import styles from './HomePage.module.css';

const HomePage = () => {
  return (
    <div className={styles.homePage}>
      {/* Hero Section */}
      <header className={styles.hero}>
        <div className={styles.heroOverlay}></div>
        <div className={styles.heroContent}>
          <h1>Bienvenido a Hoteles VERANUM</h1>
          <p>Tu refugio de confort y elegancia. Modernizando la tradición para tu mejor estadía.</p>
          <Link to="/reservar">
            <button className={styles.heroButton}>Reservar Ahora</button>
          </Link>
        </div>
      </header>

      {/* --- SECCIÓN ACTUALIZADA --- */}
      <section className={`${styles.features} container`}>
        <h2>Nuestras Habitaciones de Lujo</h2>
        <div className={styles.featuresGrid}>

          {/* Tarjeta para Habitación Doble */}
          <div className={styles.featureCard}>
            <h3>Doble Clásica</h3>
            <p>Perfecta para amigos o colegas, equipada con dos cómodas camas individuales.</p>
            <Link to="/habitacion/doble">
              <button className={styles.detailsButton}>Ver Detalles</button>
            </Link>
          </div>

          {/* Tarjeta para Habitación Matrimonial */}
          <div className={styles.featureCard}>
            <h3>Matrimonial Superior</h3>
            <p>Un refugio de elegancia y confort, con una cama King-size y vistas a la ciudad.</p>
            <Link to="/habitacion/matrimonial">
              <button className={styles.detailsButton}>Ver Detalles</button>
            </Link>
          </div>

          {/* Tarjeta para Habitación Individual */}
          <div className={styles.featureCard}>
            <h3>Individual Ejecutiva</h3>
            <p>Un espacio funcional y moderno, diseñado para el viajero de negocios.</p>
            <Link to="/habitacion/individual">
              <button className={styles.detailsButton}>Ver Detalles</button>
            </Link>
          </div>

        </div>
      </section>
    </div>
  );
};

export default HomePage;
