// src/pages/HabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAllHabitaciones } from '../services/habitacionesService';
import styles from './HabitacionesPage.module.css';

// --- COMPONENTE INTERNO ---
const RoomDetailCard = ({ habitacion }) => {
  const roomDetails = {
    "Doble Clásica": {
      image: 'https://placehold.co/1200x600/1565c0/ffffff?text=Habitacion+Doble',
      description: "Una habitación espaciosa y luminosa, equipada con dos cómodas camas individuales, perfecta para amigos o colegas que viajan juntos. El diseño funcional y el ambiente acogedor garantizan una estadía placentera y reparadora.",
      amenities: ["WiFi de alta velocidad", "TV Cable HD", "Aire Acondicionado", "Servicio a la habitación", "Baño privado con amenities"]
    },
    "Matrimonial Superior": {
      image: 'https://placehold.co/1200x600/0d47a1/ffffff?text=Habitacion+Matrimonial',
      description: "Un refugio de elegancia y confort, con una cama King-size, vistas a la ciudad y un baño de lujo para una estadía romántica. Ideal para parejas que buscan una experiencia inolvidable.",
      amenities: ["Cama King-size", "Bañera de hidromasaje", "Smart TV 55\" con Netflix", "Minibar de cortesía", "Cafetera Nespresso"]
    },
    "Individual Ejecutiva": {
      image: 'https://placehold.co/1200x600/6c757d/ffffff?text=Habitacion+Individual',
      description: "Un espacio funcional y moderno, diseñado para el viajero de negocios. Cuenta con un amplio escritorio, silla ergonómica y todas las comodidades para asegurar productividad y descanso.",
      amenities: ["WiFi de alta velocidad", "Escritorio de trabajo", "Caja de seguridad para laptop", "Plancha y tabla de planchar", "Acceso a business center"]
    }
  };

  const details = roomDetails[habitacion.nombre] || {};

  return (
    <div className={styles.roomDetailCard}>
      <div className={styles.roomImage}>
        <img src={details.image || `https://placehold.co/1200x600/333/fff?text=${habitacion.nombre.replace(' ', '+')}`} alt={habitacion.nombre} />
      </div>
      <div className={styles.roomInfo}>
        <h2>{habitacion.nombre}</h2>
        <p className={styles.roomPrice}>${habitacion.precio_noche.toFixed(0)} / noche</p>
        <p className={styles.roomDescription}>{details.description || habitacion.descripcion}</p>
        
        <h3>Servicios Incluidos</h3>
        <ul className={styles.amenitiesList}>
          {(details.amenities || habitacion.servicios).map((servicio, index) => (
              <li key={index}>{servicio}</li>
          ))}
        </ul>
        
        {/* --- CAMBIO: PASAMOS EL OBJETO 'habitacion' EN EL ESTADO DEL ENLACE --- */}
        <Link to="/reservar" state={{ preselectedRoom: habitacion }}>
          <button className={styles.reserveButton}>Reservar Esta Habitación</button>
        </Link>
      </div>
    </div>
  );
};


// --- Componente Principal de la Página ---
const HabitacionesPage = () => {
  const [habitaciones, setHabitaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHabitaciones = async () => {
      try {
        const result = await getAllHabitaciones();
        if (result && Array.isArray(result.data)) {
          setHabitaciones(result.data);
        } else {
          setError('No se pudo obtener la lista de habitaciones.');
        }
      } catch (err) {
        setError('No se pudieron cargar las habitaciones en este momento.');
      } finally {
        setLoading(false);
      }
    };
    fetchHabitaciones();
  }, []);

  if (loading) return <div className="container"><p>Cargando habitaciones...</p></div>;
  if (error) return <div className="container"><p style={{color: 'red', textAlign: 'center'}}>{error}</p></div>;

  return (
    <div className="container">
      <div className={styles.header}>
        <h1>Nuestras Habitaciones</h1>
        <p>Encuentra el espacio perfecto diseñado para tu confort y descanso.</p>
      </div>
      <div className={styles.roomList}>
        {habitaciones.length > 0 ? (
          habitaciones.map(h => <RoomDetailCard key={h._id} habitacion={h} />)
        ) : (
          <p style={{textAlign: 'center'}}>No hay habitaciones para mostrar en este momento.</p>
        )}
      </div>
    </div>
  );
};

export default HabitacionesPage;
