// src/pages/ManageHabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { getAllHabitaciones } from '../services/habitacionesService';
import { createHabitacion, deleteHabitacion } from '../services/adminService';
import HabitacionModal from '../components/admin/HabitacionModal'; // <-- 1. IMPORTA LA MODAL
import styles from './ManageReservasPage.module.css'; // Reutilizamos los estilos

const ManageHabitacionesPage = () => {
    const [habitaciones, setHabitaciones] = useState([]);
    const [filteredHabitaciones, setFilteredHabitaciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- Estados para la Modal ---
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingHabitacion, setEditingHabitacion] = useState(null);

    // --- Estados para los filtros ---
    const [searchTerm, setSearchTerm] = useState('');
    const [capacityFilter, setCapacityFilter] = useState('');

    useEffect(() => {
        fetchHabitaciones();
    }, []);

    const fetchHabitaciones = async () => {
        try {
            setLoading(true);
            const response = await getAllHabitaciones();
            setHabitaciones(response.data);
            setFilteredHabitaciones(response.data);
        } catch (err) {
            setError('No se pudieron cargar las habitaciones.');
        } finally {
            setLoading(false);
        }
    };
    
    // --- Lógica de filtrado ---
    useEffect(() => {
        let result = habitaciones;
        if (searchTerm) {
            result = result.filter(h => h.nombre.toLowerCase().includes(searchTerm.toLowerCase()));
        }
        if (capacityFilter) {
            result = result.filter(h => h.capacidad >= Number(capacityFilter));
        }
        setFilteredHabitaciones(result);
    }, [searchTerm, capacityFilter, habitaciones]);

    // --- Funciones para manejar la Modal ---
    const handleOpenModal = (habitacion = null) => {
        setEditingHabitacion(habitacion);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setEditingHabitacion(null);
    };

    const handleSaveHabitacion = async (formData) => {
        try {
            if (editingHabitacion) {
                // Lógica para actualizar (a implementar en el futuro)
            } else {
                await createHabitacion(formData);
            }
            fetchHabitaciones();
            handleCloseModal();
        } catch (err) {
            alert('Error al guardar la habitación.');
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar esta habitación?')) {
            try {
                await deleteHabitacion(id);
                fetchHabitaciones();
            } catch (err) {
                alert('No se pudo eliminar la habitación.');
            }
        }
    };
    
    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Habitaciones</h1>
                {/* 2. CONECTA EL BOTÓN A LA MODAL */}
                <button onClick={() => handleOpenModal()} className={styles.addButton}>
                    Crear Nueva Habitación
                </button>
            </div>

            {/* --- Barra de Filtros --- */}
            <div className={`card ${styles.filtersCard}`}>
                <input
                    type="text"
                    placeholder="Buscar por nombre de habitación..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Capacidad mínima..."
                    value={capacityFilter}
                    onChange={(e) => setCapacityFilter(e.target.value)}
                />
            </div>
            
            {/* --- Tabla de Habitaciones --- */}
            <div className={styles.tableContainer}>
                {loading && <p>Cargando...</p>}
                {error && <p className="error-message">{error}</p>}
                {!loading && (
                    <table>
                        <thead>
                            <tr>
                                <th>Nombre</th>
                                <th>Capacidad</th>
                                <th>Piso</th>
                                <th>Precio / Noche</th>
                                <th>Estado</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredHabitaciones.length > 0 ? filteredHabitaciones.map(h => (
                                <tr key={h._id}>
                                    <td data-label="Nombre">{h.nombre}</td>
                                    <td data-label="Capacidad">{h.capacidad}</td>
                                    <td data-label="Piso">{h.piso}</td>
                                    <td data-label="Precio">${h.precio_noche.toFixed(0)}</td>
                                    <td data-label="Estado">
                                        <span className={`${styles.status} ${h.disponible ? styles.status_confirmado : styles.status_cancelado}`}>
                                            {h.disponible ? 'Disponible' : 'No Disponible'}
                                        </span>
                                    </td>
                                    <td data-label="Acciones">
                                        <button className={styles.actionButton}>Editar</button>
                                        <button onClick={() => handleDelete(h._id)} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                    </td>
                                </tr>
                            )) : (
                                <tr>
                                    <td colSpan="6">No se encontraron habitaciones con los filtros actuales.</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
            
            {/* 3. RENDERIZA LA MODAL */}
            <HabitacionModal 
                show={isModalOpen}
                onClose={handleCloseModal}
                onSave={handleSaveHabitacion}
                habitacion={editingHabitacion}
            />
        </div>
    );
};

export default ManageHabitacionesPage;
