import React, { useState, useEffect } from 'react';
import { getAllReservasAdmin } from '../../services/adminService';
import { Link } from 'react-router-dom';
import styles from './GestionReservasPage.module.css';
import { formatDate } from '../../utils/helpers';
import Notification from '../../components/common/Notification';
import ConfirmationModal from '../../components/common/ConfirmationModal';

// --- Iconos para la UI ---
const CalendarIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect><line x1="16" y1="2" x2="16" y2="6"></line><line x1="8" y1="2" x2="8" y2="6"></line><line x1="3" y1="10" x2="21" y2="10"></line></svg>;
const UserIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path><circle cx="12" cy="7" r="4"></circle></svg>;
const KeyIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M10 18s-4-6 2-6c6 0 6 7-2 7S10 18 10 18z"></path><rect x="1" y="5" width="22" height="12" rx="2" ry="2"></rect><circle cx="8" cy="11" r="1"></circle><circle cx="16" cy="11" r="1"></circle></svg>;
const HomeIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path><polyline points="9 22 9 12 15 12 15 22"></polyline></svg>;
const DollarSignIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><line x1="12" y1="1" x2="12" y2="23"></line><path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path></svg>;
const CheckCircleIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M22 11.08V12a10 10 0 1 1-22 0c0-9.54 8.57-11.92 11.48-2.09a2 2 0 0 1 0 2.83 10 10 0 0 0 2.14 9.88z"></path><polyline points="22 4 12 14.01 9 11.01"></polyline></svg>;
const XCircleIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="12" r="10"></circle><line x1="15" y1="9" x2="9" y2="15"></line><line x1="9" y1="9" x2="15" y2="15"></line></svg>;
const ClockIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="12" r="10"></circle><polyline points="12 6 12 12 16 14"></polyline></svg>;
const EditIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V9l-5-5z"></path><polyline points="18 2 22 6 12 16 8 12"></polyline></svg>;
const TrashIcon = () => <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path><line x1="10" y1="11" x2="10" y2="17"></line><line x1="14" y1="11" x2="14" y2="17"></line></svg>;


const GestionReservasPage = () => {
    const [reservas, setReservas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [filters, setFilters] = useState({
        search: '',
        estado: 'Todos',
        sortBy: 'numeroReserva_desc',
        fecha: ''
    });
    const [notification, setNotification] = useState({ show: false, message: '', type: '' });
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [reservaToDelete, setReservaToDelete] = useState(null);

    useEffect(() => {
        const fetchReservas = async () => {
            try {
                const response = await getAllReservasAdmin();
                setReservas(response.data);
            } catch (err) {
                setError('No se pudieron cargar las reservas.');
                console.error("Error fetching reservas:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchReservas();
    }, []);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prevFilters => ({ ...prevFilters, [name]: value }));
    };

    const handleClearFilters = () => {
        setFilters({ search: '', estado: 'Todos', sortBy: 'numeroReserva_desc', fecha: '' });
    };

    const filteredReservas = () => {
        return reservas
            .filter(reserva => {
                const searchRegex = new RegExp(filters.search, 'i');
                const matchesSearch = searchRegex.test(reserva.numeroReserva) ||
                                      searchRegex.test(reserva.cliente_id?.nombre) ||
                                      searchRegex.test(reserva.cliente_id?._id);
                const matchesEstado = filters.estado === 'Todos' || reserva.estado === filters.estado;
                const matchesFecha = filters.fecha === '' || formatDate(reserva.fecha_inicio).includes(filters.fecha) || formatDate(reserva.fecha_fin).includes(filters.fecha);
                return matchesSearch && matchesEstado && matchesFecha;
            })
            .sort((a, b) => {
                const [field, direction] = filters.sortBy.split('_');
                const factor = direction === 'asc' ? 1 : -1;
                if (field === 'numeroReserva') {
                    return factor * (parseInt(a.numeroReserva) - parseInt(b.numeroReserva));
                }
                return 0;
            });
    };

    const handleDeleteClick = (reserva) => {
        setReservaToDelete(reserva);
        setShowDeleteModal(true);
    };

    const confirmDelete = async () => {
        if (!reservaToDelete) return;
        // Implementar la lógica de eliminación aquí (llamada al servicio)
        console.log(`Eliminar reserva con ID: ${reservaToDelete._id}`);
        setShowDeleteModal(false);
        setNotification({ show: true, message: `Reserva N°${reservaToDelete.numeroReserva} eliminada.`, type: 'success' });
        // Después de eliminar, podrías recargar las reservas
    };

    const closeNotification = () => {
        setNotification({ ...notification, show: false });
    };

    if (loading) {
        return <div className="container"><p>Cargando reservas...</p></div>;
    }

    if (error) {
        return (
            <div className="container">
                <div className={styles.filtersContainer}>
                    <div className={styles.search}>
                        <label htmlFor="search">Buscar Reserva</label>
                        <input type="text" id="search" name="search" value={filters.search} onChange={handleFilterChange} placeholder="RUN, N° Reserva o Nombre..." />
                    </div>
                    <div className={styles.filter}>
                        <label htmlFor="estado">Filtrar por Estado</label>
                        <select id="estado" name="estado" value={filters.estado} onChange={handleFilterChange}>
                            <option value="Todos">Todos los Estados</option>
                            <option value="Confirmado">Confirmado</option>
                            <option value="Pendiente">Pendiente</option>
                            <option value="Cancelado">Cancelado</option>
                        </select>
                    </div>
                    <div className={styles.sort}>
                        <label htmlFor="sortBy">Ordenar por N° Reserva</label>
                        <select id="sortBy" name="sortBy" value={filters.sortBy} onChange={handleFilterChange}>
                            <option value="numeroReserva_desc">Mayor a Menor</option>
                            <option value="numeroReserva_asc">Menor a Mayor</option>
                        </select>
                    </div>
                    <div className={styles.date}>
                        <label htmlFor="fecha">Buscar por Fecha</label>
                        <input type="text" id="fecha" name="fecha" value={filters.fecha} onChange={handleFilterChange} placeholder="dd-mm-aaaa" />
                    </div>
                    <button className={styles.clearFiltersButton} onClick={handleClearFilters}>Borrar Filtros</button>
                </div>
                <div className={styles.errorContainer}>
                    <p className={styles.errorMessage}>{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="container">
            <Notification show={notification.show} message={notification.message} type={notification.type} onClose={closeNotification} />
            <ConfirmationModal
                show={showDeleteModal}
                title="Confirmar Eliminación"
                message={`¿Estás seguro de que deseas eliminar la reserva N°${reservaToDelete?.numeroReserva}? Esta acción es irreversible.`}
                onConfirm={confirmDelete}
                onCancel={() => setShowDeleteModal(false)}
                confirmText="Sí, Eliminar"
                cancelText="No, Cancelar"
            />
            <div className={styles.header}>
                <h1>Gestión de Reservas</h1>
                <Link to="/admin/reservas/crear" className={styles.createButton}>Crear Nueva Reserva</Link>
            </div>
            <div className={styles.filtersContainer}>
                <div className={styles.search}>
                    <label htmlFor="search">Buscar Reserva</label>
                    <input type="text" id="search" name="search" value={filters.search} onChange={handleFilterChange} placeholder="RUN, N° Reserva o Nombre..." />
                </div>
                <div className={styles.filter}>
                    <label htmlFor="estado">Filtrar por Estado</label>
                    <select id="estado" name="estado" value={filters.estado} onChange={handleFilterChange}>
                        <option value="Todos">Todos los Estados</option>
                        <option value="Confirmado">Confirmado</option>
                        <option value="Pendiente">Pendiente</option>
                        <option value="Cancelado">Cancelado</option>
                    </select>
                </div>
                <div className={styles.sort}>
                    <label htmlFor="sortBy">Ordenar por N° Reserva</label>
                    <select id="sortBy" name="sortBy" value={filters.sortBy} onChange={handleFilterChange}>
                        <option value="numeroReserva_desc">Mayor a Menor</option>
                        <option value="numeroReserva_asc">Menor a Mayor</option>
                    </select>
                </div>
                <div className={styles.date}>
                    <label htmlFor="fecha">Buscar por Fecha</label>
                    <input type="text" id="fecha" name="fecha" value={filters.fecha} onChange={handleFilterChange} placeholder="dd-mm-aaaa" />
                    <button className={styles.clearFiltersButton} onClick={handleClearFilters}>Borrar Filtros</button>
                </div>
            </div>

            <div className={styles.reservasGrid}>
                {filteredReservas().map(reserva => (
                    <div key={reserva._id} className={`${styles.reservaCard} card`}>
                        <div className={styles.cardHeader}>
                            <span className={styles.reservaId}><KeyIcon /> Reserva N°{reserva.numeroReserva}</span>
                            <span className={`${styles.status} ${styles[`status_${reserva.estado?.toLowerCase()}`]}`}>{reserva.estado}</span>
                        </div>
                        <div className={styles.cardBody}>
                            <p><UserIcon /> Cliente: {reserva.cliente_id?.nombre || 'N/A'} ({reserva.cliente_id?._id || 'N/A'})</p>
                            <p><HomeIcon /> Hotel: {reserva.hotel_id?.nombre || 'N/A'}</p>
                            <p><KeyIcon /> Habitación: {reserva.habitacion_id?.nombre || 'N/A'}</p>
                            <p><CalendarIcon /> Check-in: {formatDate(reserva.fecha_inicio)}</p>
                            <p><CalendarIcon /> Check-out: {formatDate(reserva.fecha_fin)}</p>
                            <p><DollarSignIcon /> Total: ${reserva.precio_final?.toLocaleString('es-CL') || 'N/A'}</p>
                            {reserva.servicios_adicionales && reserva.servicios_adicionales.length > 0 && (
                                <div className={styles.serviciosAdicionales}>
                                    <p><span style={{ marginRight: '0.5rem' }}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M12 2L14.5 9.5L22 12L14.5 14.5L12 22L9.5 14.5L2 12L9.5 9.5L12 2z"></path></svg></span> Servicios:</p>
                                    <ul className={styles.serviciosLista}>
                                        {reserva.servicios_adicionales.map(servicio => (
                                            <li key={servicio._id}>{servicio.nombre}</li>
                                        ))}
                                    </ul>
                                </div>
                            )}
                        </div>
                        <div className={styles.cardFooter}>
                            <Link to={`/admin/reservas/editar/${reserva._id}`} className={styles.editButton}><EditIcon /> Editar</Link>
                            <button onClick={() => handleDeleteClick(reserva)} className={styles.deleteButton}><TrashIcon /> Eliminar</button>
                        </div>
                    </div>
                ))}
            </div>

            {filteredReservas().length === 0 && !loading && !error && (
                <div className={styles.noResults}>
                    <p>No se encontraron reservas con los filtros aplicados.</p>
                </div>
            )}
        </div>
    );
};

export default GestionReservasPage;
