// src/components/layout/Navbar.jsx
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', padding: '10px', background: '#eee' }}>
      <Link to="/">VERANUM</Link>
      <div>
        <Link to="/reservar" style={{ marginRight: '10px' }}>Reservar</Link>
        {user && (user.rol === 'admin' || user.rol === 'gerente') && (
            <Link to="/admin" style={{ marginRight: '10px' }}>Admin</Link>
        )}
        {user ? (
          <>
            <span style={{ marginRight: '10px' }}>Hola, {user.nombre}</span>
            <button onClick={handleLogout}>Salir</button>
          </>
        ) : (
          <Link to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
};
export default Navbar;
