// src/pages/ReservationsPage.jsx
import React, { useState, useContext, useEffect, useMemo } from 'react';
import { createReservation } from '../services/reservasService';
import { getServiciosByHotelAdmin } from '../services/adminService'; // Usamos el servicio de admin que ya tiene la función
import AuthContext from '../context/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';
import DateRangePicker from '../components/reservations/DateRangePicker';
import Notification from '../components/common/Notification';
import styles from './ReservationsPage.module.css';

const ReservationsPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    
    const preselectedRoom = location.state?.preselectedRoom;

    const [fechas, setFechas] = useState({ inicio: '', fin: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { user } = useContext(AuthContext);
    const [notification, setNotification] = useState({ show: false, message: '', type: '' });

    // --- NUEVOS ESTADOS PARA SERVICIOS ---
    const [serviciosDisponibles, setServiciosDisponibles] = useState([]);
    const [serviciosSeleccionados, setServiciosSeleccionados] = useState(new Set());

    // Cargar servicios disponibles cuando tengamos la habitación preseleccionada
    useEffect(() => {
        if (preselectedRoom?.hotel_id) {
            getServiciosByHotelAdmin(preselectedRoom.hotel_id)
                .then(response => setServiciosDisponibles(response.data))
                .catch(err => console.error("Error al cargar servicios", err));
        }
    }, [preselectedRoom]);

    useEffect(() => {
        if (notification.show) {
            const timer = setTimeout(() => {
                setNotification({ ...notification, show: false });
            }, 3000);
            return () => clearTimeout(timer);
        }
    }, [notification]);

    useEffect(() => {
        if (!preselectedRoom) {
            navigate('/habitaciones');
        }
    }, [preselectedRoom, navigate]);

    // Función para manejar la selección de servicios
    const handleServiceToggle = (servicioId) => {
        const nuevosServicios = new Set(serviciosSeleccionados);
        if (nuevosServicios.has(servicioId)) {
            nuevosServicios.delete(servicioId);
        } else {
            nuevosServicios.add(servicioId);
        }
        setServiciosSeleccionados(nuevosServicios);
    };

    // --- LÓGICA DE CÁLCULO DE PRECIO MEJORADA CON useMemo ---
    const { diasEstadia, precioTotal } = useMemo(() => {
        if (!fechas.inicio || !fechas.fin) return { diasEstadia: 0, precioTotal: 0 };

        const dias = Math.max(1, Math.ceil((new Date(fechas.fin) - new Date(fechas.inicio)) / (1000 * 60 * 60 * 24)));
        const costoBase = dias * (preselectedRoom?.precio || 0);

        const costoServicios = dias * serviciosDisponibles
            .filter(s => serviciosSeleccionados.has(s._id))
            .reduce((total, s) => total + s.precio_diario, 0);

        return { diasEstadia: dias, precioTotal: costoBase + costoServicios };
    }, [fechas, preselectedRoom, serviciosSeleccionados, serviciosDisponibles]);


    const handleConfirm = async () => {
        if (!user) {
            navigate('/login', { state: { from: location } });
            return;
        }
        if (!fechas.inicio || !fechas.fin) {
            setError('Por favor, selecciona las fechas de tu estadía.');
            return;
        }

        setLoading(true);
        setError('');
        try {
            const reservationData = {
                habitacion_id: preselectedRoom._id,
                hotel_id: preselectedRoom.hotel_id,
                fecha_inicio: fechas.inicio,
                fecha_fin: fechas.fin,
                estado: 'Confirmado',
                servicios_adicionales: Array.from(serviciosSeleccionados)
            };
            await createReservation(reservationData);
            
            setNotification({ show: true, message: '¡Reserva Exitosa!', type: 'success' });

            setTimeout(() => {
                navigate('/');
            }, 2000);

        } catch (err) {
            const errorMessage = err.response?.data?.message || 'No se pudo procesar la reserva. Intente más tarde.';
            setError(errorMessage);
            setLoading(false);
        }
    };

    if (!preselectedRoom) {
        return <div className="container"><p>Redirigiendo...</p></div>;
    }

    return (
        <>
            <Notification 
                show={notification.show}
                message={notification.message}
                type={notification.type}
            />
            <div className="container">
                <div className={styles.reservationWrapper}>
                    <div className={styles.roomSummary}>
                        <img src={`https://placehold.co/600x400/005f73/FFF?text=${preselectedRoom.nombre.replace(' ', '+')}`} alt={preselectedRoom.nombre} />
                        <h2>{preselectedRoom.nombre}</h2>
                        <p>{preselectedRoom.descripcion}</p>
                    </div>
                    <div className={styles.bookingForm}>
                        <h3>Completa tu reserva</h3>
                        <p className={styles.formSubtitle}>Selecciona las fechas de tu estadía.</p>
                        
                        <DateRangePicker fechas={fechas} onDateChange={setFechas} />
                        
                        {error && <p className={styles.errorMessage}>{error}</p>}
                        
                        {/* --- NUEVA SECCIÓN DE SERVICIOS ADICIONALES --- */}
                        <div className={styles.servicesSection}>
                            <h4>Añade Servicios Adicionales (costo por día)</h4>
                            {serviciosDisponibles.length > 0 ? (
                                serviciosDisponibles.map(servicio => (
                                    <div key={servicio._id} className={styles.serviceItem}>
                                        <input 
                                            type="checkbox" 
                                            id={`servicio-${servicio._id}`}
                                            checked={serviciosSeleccionados.has(servicio._id)}
                                            onChange={() => handleServiceToggle(servicio._id)}
                                        />
                                        <label htmlFor={`servicio-${servicio._id}`}>
                                            {servicio.nombre}
                                            <span>+${servicio.precio_diario.toLocaleString('es-CL')}</span>
                                        </label>
                                    </div>
                                ))
                            ) : (
                                <p className={styles.noServices}>No hay servicios adicionales disponibles para este hotel.</p>
                            )}
                        </div>

                        {fechas.inicio && fechas.fin && (
                            <div className={styles.summary}>
                                <div className={styles.summaryLine}>
                                    <span>Duración:</span>
                                    <strong>{diasEstadia} {diasEstadia > 1 ? 'noches' : 'noche'}</strong>
                                </div>
                                <div className={styles.summaryLine_total}>
                                    <span>Total a Pagar:</span>
                                    <strong>${precioTotal.toLocaleString('es-CL')}</strong>
                                </div>
                            </div>
                        )}

                        <div className={styles.buttonContainer}>
                            <button onClick={handleConfirm} disabled={loading || !fechas.inicio || !fechas.fin}>
                                {loading ? 'Confirmando...' : 'Confirmar y Reservar'}
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default ReservationsPage;
