// src/pages/ManageHabitacionesPage.jsx
import React, { useState, useEffect } from 'react';
import { getAllHabitaciones } from '../services/habitacionesService';
// --- INICIO: Importaciones añadidas ---
import { createHabitacion, updateHabitacion, deleteHabitacion, getReservas } from '../services/adminService';
// --- FIN: Importaciones añadidas ---
import HabitacionModal from '../components/admin/HabitacionModal';
import styles from './ManageReservasPage.module.css'; // Reutilizamos los estilos

const ManageHabitacionesPage = () => {
    const [habitaciones, setHabitaciones] = useState([]);
    const [reservas, setReservas] = useState([]); // Estado para guardar las reservas
    const [filteredHabitaciones, setFilteredHabitaciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingHabitacion, setEditingHabitacion] = useState(null);

    // --- Estados para filtros y ordenamiento ---
    const [searchTerm, setSearchTerm] = useState('');
    const [availabilityFilter, setAvailabilityFilter] = useState('all');
    const [priceSort, setPriceSort] = useState('none'); // 'desc', 'asc', 'none'

    // Función para cargar tanto habitaciones como reservas
    const fetchData = async () => {
        try {
            setLoading(true);
            // Usamos Promise.all para cargar ambos recursos en paralelo
            const [habitacionesRes, reservasRes] = await Promise.all([
                getAllHabitaciones(),
                getReservas()
            ]);
            
            const hoy = new Date();
            hoy.setHours(0, 0, 0, 0);

            // Mapeamos las habitaciones para añadir la información del cliente actual
            const habitacionesConCliente = habitacionesRes.data.map(hab => {
                const reservaActiva = reservasRes.data.find(res => {
                    if (res.habitacion_id?._id !== hab._id || res.estado !== 'Confirmado') {
                        return false;
                    }
                    const fechaInicio = new Date(res.fecha_inicio);
                    const fechaFin = new Date(res.fecha_fin);
                    return hoy >= fechaInicio && hoy < fechaFin;
                });
                return {
                    ...hab,
                    clienteActualRUN: reservaActiva ? reservaActiva.cliente_id?._id : null
                };
            });

            setHabitaciones(habitacionesConCliente);
            setReservas(reservasRes.data);

        } catch (err) {
            setError('No se pudieron cargar los datos.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchData();
    }, []);

    // useEffect para filtrar y ordenar
    useEffect(() => {
        let result = [...habitaciones];

        // Filtro de búsqueda
        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase();
            result = result.filter(h => 
                h.nombre.toLowerCase().includes(lowercasedTerm) ||
                h.numeroHabitacion?.toString().includes(lowercasedTerm)
            );
        }

        // Filtro de disponibilidad
        if (availabilityFilter !== 'all') {
            result = result.filter(h => h.disponible === (availabilityFilter === 'true'));
        }

        // Ordenamiento por precio
        if (priceSort !== 'none') {
            result.sort((a, b) => {
                return priceSort === 'desc' 
                    ? b.precio_noche - a.precio_noche 
                    : a.precio_noche - b.precio_noche;
            });
        }

        setFilteredHabitaciones(result);
    }, [searchTerm, availabilityFilter, priceSort, habitaciones]);

    const handleOpenModal = (habitacion = null) => {
        setEditingHabitacion(habitacion);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setEditingHabitacion(null);
    };

    const handleSaveHabitacion = async (formData) => {
        try {
            if (editingHabitacion) {
                await updateHabitacion(editingHabitacion._id, formData);
            } else {
                await createHabitacion(formData);
            }
            fetchData(); // Recargamos todos los datos
            handleCloseModal();
        } catch (err) {
            alert('Error al guardar la habitación.');
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar esta habitación?')) {
            try {
                await deleteHabitacion(id);
                fetchData(); // Recargamos todos los datos
            } catch (err) {
                alert('No se pudo eliminar la habitación.');
            }
        }
    };
    
    return (
        <div className="container">
            <div className={styles.header}>
                <h1>Gestión de Habitaciones</h1>
                <button onClick={() => handleOpenModal()} className={styles.addButton}>
                    Crear Nueva Habitación
                </button>
            </div>
            
            <div className={`card ${styles.filtersCard}`}>
                <div className={styles.filterGroup}>
                    <label>Buscar</label>
                    <input type="text" placeholder="Por Nº Habitación o Nombre..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
                </div>
                <div className={styles.filterGroup}>
                    <label>Filtrar por Disponibilidad</label>
                    <select value={availabilityFilter} onChange={(e) => setAvailabilityFilter(e.target.value)}>
                        <option value="all">Toda Disponibilidad</option>
                        <option value="true">Disponible</option>
                        <option value="false">No Disponible</option>
                    </select>
                </div>
                {/* --- INICIO: NUEVO FILTRO POR PRECIO --- */}
                <div className={styles.filterGroup}>
                    <label>Ordenar por Precio</label>
                    <select value={priceSort} onChange={(e) => setPriceSort(e.target.value)}>
                        <option value="none">Por Defecto</option>
                        <option value="desc">Mayor a Menor</option>
                        <option value="asc">Menor a Mayor</option>
                    </select>
                </div>
                {/* --- FIN: NUEVO FILTRO POR PRECIO --- */}
            </div>

            <div className={styles.tableContainer}>
                {loading && <p>Cargando...</p>}
                {error && <p className={styles.errorMessage}>{error}</p>}
                {!loading && !error && (
                    <table>
                        <thead>
                            <tr>
                                <th>Nº Habitación</th>
                                <th>Nombre</th>
                                <th>Capacidad</th>
                                <th>Piso</th>
                                <th>Precio / Noche</th>
                                <th>Estado</th>
                                <th>Cliente Actual</th> {/* --- NUEVA COLUMNA --- */}
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredHabitaciones.length > 0 ? filteredHabitaciones.map(h => (
                                <tr key={h._id}>
                                    <td data-label="Nº Habitación">{h.numeroHabitacion}</td>
                                    <td data-label="Nombre">{h.nombre}</td>
                                    <td data-label="Capacidad">{h.capacidad}</td>
                                    <td data-label="Piso">{h.piso}</td>
                                    <td data-label="Precio">${new Intl.NumberFormat('es-CL').format(h.precio_noche)}</td>
                                    <td data-label="Estado"><span className={`${styles.status} ${h.disponible ? styles.status_confirmado : styles.status_cancelado}`}>{h.disponible ? 'Disponible' : 'No Disponible'}</span></td>
                                    {/* --- NUEVO DATO EN LA TABLA --- */}
                                    <td data-label="Cliente Actual">{h.clienteActualRUN || 'N/A'}</td>
                                    <td data-label="Acciones">
                                        {/* --- INICIO: BOTONES AJUSTADOS --- */}
                                        <div className={styles.actionsContainer}>
                                            <button onClick={() => handleOpenModal(h)} className={styles.actionButton}>Editar</button>
                                            <button onClick={() => handleDelete(h._id)} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                        </div>
                                        {/* --- FIN: BOTONES AJUSTADOS --- */}
                                    </td>
                                </tr>
                            )) : (
                                <tr><td colSpan="8">No se encontraron habitaciones.</td></tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
            <HabitacionModal show={isModalOpen} onClose={handleCloseModal} onSave={handleSaveHabitacion} habitacion={editingHabitacion} />
        </div>
    );
};

export default ManageHabitacionesPage;
