// src/pages/ManageUsuariosPage.jsx
import React, { useState, useEffect } from 'react';
import { getAllUsers, getReservasByRun, createUserAdmin } from '../services/adminService';
import UserReservationsModal from '../components/admin/UserReservationsModal';
import UserModal from '../components/admin/UserModal';
import Notification from '../components/common/Notification'; // <-- 1. Se importa el nuevo componente
import styles from './ManageReservasPage.module.css';

const ManageUsuariosPage = () => {
    const [users, setUsers] = useState([]);
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    
    const [searchTerm, setSearchTerm] = useState('');
    const [roleFilter, setRoleFilter] = useState('all');
    const [reservasSort, setReservasSort] = useState('default');
    const [antiguedadSort, setAntiguedadSort] = useState('default');

    const [isReservationsModalOpen, setIsReservationsModalOpen] = useState(false);
    const [modalLoading, setModalLoading] = useState(false);
    const [selectedUserForReservations, setSelectedUserForReservations] = useState(null);
    const [selectedUserReservations, setSelectedUserReservations] = useState([]);

    const [isUserModalOpen, setIsUserModalOpen] = useState(false);
    const [editingUser, setEditingUser] = useState(null);

    // --- 2. Se añade el estado para la notificación ---
    const [notification, setNotification] = useState({ show: false, message: '', type: '' });

    // useEffect para ocultar la notificación después de unos segundos
    useEffect(() => {
        if (notification.show) {
            const timer = setTimeout(() => {
                setNotification({ show: false, message: '', type: '' });
            }, 3000); // El mensaje desaparecerá después de 3 segundos
            return () => clearTimeout(timer);
        }
    }, [notification]);

    const fetchUsers = async () => {
        try {
            setLoading(true);
            const response = await getAllUsers();
            setUsers(response.data);
            setError('');
        } catch (err) {
            setError('No se pudieron cargar los usuarios.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    useEffect(() => {
        let result = [...users];

        if (searchTerm) {
            const lowercasedTerm = searchTerm.toLowerCase().trim();
            result = result.filter(user => user._id.toLowerCase().includes(lowercasedTerm));
        }
        if (roleFilter !== 'all') {
            result = result.filter(user => user.rol === roleFilter);
        }
        if (reservasSort !== 'default') {
            result.sort((a, b) => reservasSort === 'desc' ? b.numeroDeReservas - a.numeroDeReservas : a.numeroDeReservas - b.numeroDeReservas);
        } else if (antiguedadSort !== 'default') {
            result.sort((a, b) => antiguedadSort === 'asc' ? new Date(a.createdAt) - new Date(b.createdAt) : new Date(b.createdAt) - new Date(a.createdAt));
        }
        setFilteredUsers(result);
    }, [users, searchTerm, roleFilter, reservasSort, antiguedadSort]);

    const handleClearFilters = () => {
        setSearchTerm('');
        setRoleFilter('all');
        setReservasSort('default');
        setAntiguedadSort('default');
    };

    const handleOpenReservasModal = async (user) => {
        setSelectedUserForReservations(user);
        setIsReservationsModalOpen(true);
        setModalLoading(true);
        try {
            const response = await getReservasByRun(user._id);
            setSelectedUserReservations(response.data);
        } catch (err) {
            setNotification({ show: true, message: 'No se pudo cargar el historial de reservas.', type: 'error' });
        } finally {
            setModalLoading(false);
        }
    };

    const handleCloseReservasModal = () => {
        setIsReservationsModalOpen(false);
        setSelectedUserForReservations(null);
        setSelectedUserReservations([]);
    };

    const handleOpenUserModal = (user = null) => {
        setEditingUser(user);
        setIsUserModalOpen(true);
    };

    const handleCloseUserModal = () => {
        setEditingUser(null);
        setIsUserModalOpen(false);
    };

    const handleSaveUser = async (formData) => {
        try {
            if (editingUser) {
                // Lógica de actualización (a futuro)
                setNotification({ show: true, message: 'Función para editar pendiente.', type: 'error' });
            } else {
                // --- 3. Se reemplaza el alert() por la nueva notificación ---
                await createUserAdmin(formData);
                setNotification({ show: true, message: 'Usuario Creado Exitosamente', type: 'success' });
            }
            fetchUsers();
            handleCloseUserModal();
        } catch (err) {
            setNotification({ show: true, message: `Error: ${err.response?.data?.message || err.message}`, type: 'error' });
        }
    };

    const formatDate = (dateString) => new Date(dateString).toLocaleDateString('es-CL');

    return (
        // Se usa un Fragment <> para poder tener la notificación al mismo nivel que el container
        <>
            <Notification 
                show={notification.show} 
                message={notification.message} 
                type={notification.type} 
            />
            <div className="container">
                <div className={styles.header}>
                    <h1>Gestión de Usuarios</h1>
                    <button onClick={() => handleOpenUserModal(null)} className={styles.addButton}>
                        Crear Nuevo Usuario
                    </button>
                </div>
                
                <div className={`card ${styles.filtersCard}`}>
                    <div className={styles.searchContainer}>
                        <div className={styles.filterGroup}>
                            <label htmlFor="search">Buscar por RUN</label>
                            <input id="search" type="text" placeholder="Ingrese RUN del cliente..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
                        </div>
                        <button onClick={handleClearFilters} className={styles.clearFiltersButton}>
                            Borrar Filtros
                        </button>
                    </div>
                    <div className={styles.filterGroup}>
                        <label htmlFor="roleFilter">Filtrar por Rol</label>
                        <select id="roleFilter" value={roleFilter} onChange={(e) => setRoleFilter(e.target.value)}>
                            <option value="all">Todos los Roles</option>
                            <option value="cliente">Cliente</option>
                            <option value="gerente">Gerente</option>
                            <option value="admin">Admin</option>
                        </select>
                    </div>
                    <div className={styles.filterGroup}>
                        <label htmlFor="antiguedadSort">Filtrar por Antigüedad</label>
                        <select id="antiguedadSort" value={antiguedadSort} onChange={(e) => setAntiguedadSort(e.target.value)}>
                            <option value="default">Por Defecto</option>
                            <option value="desc">Más Nuevo</option>
                            <option value="asc">Más Antiguo</option>
                        </select>
                    </div>
                    <div className={styles.filterGroup}>
                        <label htmlFor="reservasSort">Ordenar por Nº Reservas</label>
                        <select id="reservasSort" value={reservasSort} onChange={(e) => setReservasSort(e.target.value)}>
                            <option value="default">Por Defecto</option>
                            <option value="desc">Mayor a Menor</option>
                            <option value="asc">Menor a Mayor</option>
                        </select>
                    </div>
                </div>

                <div className={styles.tableContainer}>
                    {loading ? <p>Cargando...</p> : error ? <p className={styles.errorMessage}>{error}</p> : (
                        <table>
                            <thead>
                                <tr>
                                    <th>RUN Cliente</th>
                                    <th>Nombre</th>
                                    <th>Email</th>
                                    <th>Rol</th>
                                    <th>Miembro Desde</th>
                                    <th>Nº Reservas</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredUsers.length > 0 ? filteredUsers.map(user => (
                                    <tr key={user._id}>
                                        <td data-label="RUN Cliente">{user._id}</td>
                                        <td data-label="Nombre">{user.nombre}</td>
                                        <td data-label="Email">{user.email}</td>
                                        <td data-label="Rol">{user.rol}</td>
                                        <td data-label="Miembro Desde">{formatDate(user.createdAt)}</td>
                                        <td data-label="Nº Reservas">
                                            {user.numeroDeReservas > 0 ? (
                                                <button onClick={() => handleOpenReservasModal(user)} className={styles.viewReservaButton}>
                                                    Ver ({user.numeroDeReservas})
                                                </button>
                                            ) : ( '0' )}
                                        </td>
                                        <td data-label="Acciones">
                                            <div className={styles.actionsContainer}>
                                                <button onClick={() => handleOpenUserModal(user)} className={styles.actionButton}>Editar</button>
                                                <button onClick={() => alert('Función para eliminar pendiente.')} className={`${styles.actionButton} ${styles.deleteButton}`}>Eliminar</button>
                                            </div>
                                        </td>
                                    </tr>
                                )) : (
                                    <tr><td colSpan="7">No se encontraron usuarios.</td></tr>
                                )}
                            </tbody>
                        </table>
                    )}
                </div>
                <UserReservationsModal 
                    show={isReservationsModalOpen}
                    onClose={handleCloseReservasModal}
                    user={selectedUserForReservations}
                    reservations={selectedUserReservations}
                    loading={modalLoading}
                />
                <UserModal
                    show={isUserModalOpen}
                    onClose={handleCloseUserModal}
                    onSave={handleSaveUser}
                    user={editingUser}
                />
            </div>
        </>
    );
};

export default ManageUsuariosPage;
