import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../services/api'; // Asegúrate de que la ruta a tu api sea correcta

// 1. Crear el Contexto
const AuthContext = createContext(null);

// 2. Crear y EXPORTAR el hook personalizado para un uso fácil
export const useAuth = () => {
    return useContext(AuthContext);
};

// 3. Crear el componente Proveedor
export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(() => {
        // Intentar obtener el usuario del localStorage al cargar
        try {
            const storedUser = localStorage.getItem('user');
            return storedUser ? JSON.parse(storedUser) : null;
        } catch (error) {
            console.error("Fallo al parsear el usuario desde localStorage", error);
            return null;
        }
    });

    const login = async (credentials) => {
        const response = await api.post('/auth/login', credentials);
        if (response.data.success) {
            const userData = {
                nombre: response.data.nombre,
                token: response.data.token
            };
            setUser(userData);
            // Guardar en localStorage
            localStorage.setItem('user', JSON.stringify(userData));
            localStorage.setItem('token', response.data.token);
        }
        return response.data;
    };

    const logout = () => {
        setUser(null);
        // Limpiar localStorage
        localStorage.removeItem('user');
        localStorage.removeItem('token');
    };

    // El valor que se comparte a través del contexto
    const value = {
        user,
        login,
        logout
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

export default AuthContext;
